// frontend/src/pages/JourneyBuilder.jsx
import React, { useCallback, useEffect, useMemo, useState } from 'react';
import ReactFlow, {
  Background,
  Controls,
  MiniMap,
  addEdge,
  useEdgesState,
  useNodesState,
  MarkerType,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import Papa from 'papaparse';

const PIN_SIZE = 14;

// ---------- helpers ----------
const uid = (p) => `${p}_${Date.now()}_${Math.floor(Math.random() * 9999)}`;
const slug = (s) =>
  String(s || '')
    .trim()
    .toLowerCase()
    .replace(/\s+/g, '_')
    .replace(/[^\w]/g, '_')
    .replace(/_+/g, '_');

const NodeShell = ({ title, subtitle, selected, children }) => (
  <div
    style={{
      background: '#fff',
      border: selected ? '2px solid #2563eb' : '1px solid #e5e7eb',
      borderRadius: 12,
      width: 320,
      boxShadow: '0 2px 8px rgba(0,0,0,.06)',
    }}
  >
    <div style={{ padding: '12px 14px', borderBottom: '1px solid #f1f5f9' }}>
      <div style={{ fontWeight: 700, color: '#0f172a' }}>{title}</div>
      {subtitle ? (
        <div style={{ fontSize: 12, color: '#64748b' }}>{subtitle}</div>
      ) : null}
    </div>
    <div style={{ padding: 12 }}>{children}</div>
  </div>
);

// ---------- node components ----------

const StartNode = ({ selected }) => (
  <NodeShell title="Start" subtitle="início do fluxo" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>
      Arraste do pino verde para o próximo nó.
    </div>
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{
        background: '#16a34a',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
  </NodeShell>
);

const EndNode = ({ selected }) => (
  <NodeShell title="End" subtitle="término do fluxo" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>
      Nó terminal. Só recebe conexões.
    </div>
    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{
        background: '#6b7280',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
  </NodeShell>
);

const MessageNode = ({ data, selected }) => (
  <NodeShell
    title={data.label || 'Mensagem'}
    subtitle={`Canal: ${String(data.channel || 'whatsapp').toUpperCase()} • Tipo: ${data.mode || 'text'}`}
    selected={selected}
  >
    <div style={{ fontSize: 12, color: '#475569' }}>
      Conteúdo configurável no painel ao lado.
    </div>

    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{
        background: '#6b7280',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{
