// frontend/src/pages/RunsPage.jsx
import React, { useEffect, useMemo, useState } from 'react';

export default function RunsPage() {
  const [runs, setRuns] = useState([]);
  const [loading, setLoading] = useState(false);

  // filtro por flow opcional (?journeyId=...)
  const journeyId = useMemo(() => new URLSearchParams(window.location.search).get('journeyId'), []);

  const load = async () => {
    setLoading(true);
    try {
      const r = await fetch('/api/runs');
      let j = await r.json();
      if (!Array.isArray(j)) j = [];
      if (journeyId) j = j.filter(x => x.journeyId === journeyId);
      setRuns(j);
    } catch (e) {
      console.error(e);
      setRuns([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
    const t = setInterval(load, 5000); // auto-refresh
    return () => clearInterval(t);
  }, []); // eslint-disable-line

  const fmt = (n) => (n ?? 0);

  return (
    <div className="page-content">
      <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center' }}>
        <h2>Execuções</h2>
        {journeyId && <div style={{ color:'#6b7280' }}>Filtrando por Journey: <code>{journeyId}</code></div>}
      </div>

      {loading && <div>Atualizando…</div>}

      <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: 16 }}>
        <thead>
          <tr style={{ textAlign: 'left', borderBottom: '1px solid #eee' }}>
            <th style={{ padding: '8px 6px' }}>Run</th>
            <th style={{ padding: '8px 6px' }}>Nome</th>
            <th style={{ padding: '8px 6px' }}>Status</th>
            <th style={{ padding: '8px 6px' }}>Fila</th>
            <th style={{ padding: '8px 6px' }}>Processados</th>
            <th style={{ padding: '8px 6px' }}>Enviados</th>
            <th style={{ padding: '8px 6px' }}>Falhas</th>
            <th style={{ padding: '8px 6px' }}>Atrasados</th>
            <th style={{ padding: '8px 6px' }}>Concluídos</th>
            <th style={{ padding: '8px 6px' }}>Atualizado em</th>
            <th style={{ padding: '8px 6px' }}>Ações</th>
          </tr>
        </thead>
        <tbody>
          {runs.map((r) => (
            <tr key={r.id} style={{ borderBottom: '1px solid #f2f2f2' }}>
              <td style={{ padding: '8px 6px', fontFamily:'monospace' }}>{r.id}</td>
              <td style={{ padding: '8px 6px' }}>{r.name || '-'}</td>
              <td style={{ padding: '8px 6px' }}>{r.status || '-'}</td>
              <td style={{ padding: '8px 6px' }}>{fmt(r.totals?.queued)}</td>
              <td style={{ padding: '8px 6px' }}>{fmt(r.totals?.processed)}</td>
              <td style={{ padding: '8px 6px' }}>{fmt(r.totals?.sent)}</td>
              <td style={{ padding: '8px 6px' }}>{fmt(r.totals?.failed)}</td>
              <td style={{ padding: '8px 6px' }}>{fmt(r.totals?.delayed)}</td>
              <td style={{ padding: '8px 6px' }}>{fmt(r.totals?.done)}</td>
              <td style={{ padding: '8px 6px', color:'#555' }}>
                {r.updatedAt ? new Date(r.updatedAt).toLocaleString() : '—'}
              </td>
              <td style={{ padding: '8px 6px' }}>
                <div style={{ display:'flex', gap:8 }}>
                  <a className="primary-btn small"
                     href={`/api/runs/${r.id}/export`}
                     target="_blank" rel="noreferrer">Exportar CSV</a>
                </div>
              </td>
            </tr>
          ))}
          {runs.length === 0 && !loading && (
            <tr><td colSpan={11} style={{ padding: 16, color:'#666' }}>Nenhuma execução encontrada.</td></tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
