// frontend/src/pages/JourneyBuilder.jsx
import React, { useCallback, useEffect, useMemo, useState } from 'react';
import ReactFlow, {
  Background,
  Controls,
  MiniMap,
  addEdge,
  useEdgesState,
  useNodesState,
  MarkerType,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import Papa from 'papaparse';

const PIN_SIZE = 14;

// ---------- helpers ----------
const uid = (p) => `${p}_${Date.now()}_${Math.floor(Math.random() * 9999)}`;
const slug = (s) =>
  String(s || '')
    .trim()
    .toLowerCase()
    .replace(/\s+/g, '_')
    .replace(/[^\w]/g, '_')
    .replace(/_+/g, '_');

const NodeShell = ({ title, subtitle, selected, children }) => (
  <div
    style={{
      background: '#fff',
      border: selected ? '2px solid #2563eb' : '1px solid #e5e7eb',
      borderRadius: 12,
      width: 320,
      boxShadow: '0 2px 8px rgba(0,0,0,.06)',
    }}
  >
    <div style={{ padding: '12px 14px', borderBottom: '1px solid #f1f5f9' }}>
      <div style={{ fontWeight: 700, color: '#0f172a' }}>{title}</div>
      {subtitle ? (
        <div style={{ fontSize: 12, color: '#64748b' }}>{subtitle}</div>
      ) : null}
    </div>
    <div style={{ padding: 12 }}>{children}</div>
  </div>
);

// ---------- node components ----------

const StartNode = ({ selected }) => (
  <NodeShell title="Start" subtitle="início do fluxo" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>
      Arraste do pino verde para o próximo nó.
    </div>
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{
        background: '#16a34a',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
  </NodeShell>
);

const EndNode = ({ selected }) => (
  <NodeShell title="End" subtitle="término do fluxo" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>
      Nó terminal. Só recebe conexões.
    </div>
    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{
        background: '#6b7280',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
  </NodeShell>
);

const MessageNode = ({ data, selected }) => (
  <NodeShell
    title={data.label || 'Mensagem'}
    subtitle={`Canal: ${String(data.channel || 'whatsapp').toUpperCase()} • Tipo: ${data.mode || 'text'}`}
    selected={selected}
  >
    <div style={{ fontSize: 12, color: '#475569' }}>
      Conteúdo configurável no painel ao lado.
    </div>

    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{
        background: '#6b7280',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{
        background: '#16a34a',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
  </NodeShell>
);

const ApiNode = ({ selected }) => (
  <NodeShell title="API Call" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>
      Configure método/URL no painel ao lado. Salva variáveis via mapeamentos.
    </div>

    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{
        background: '#6b7280',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{
        background: '#16a34a',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
  </NodeShell>
);

const CodeNode = ({ selected }) => (
  <NodeShell title="Code" subtitle="JS custom" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>
      Cole seu JS no painel ao lado. Deve retornar um objeto de variáveis.
    </div>

    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{
        background: '#6b7280',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{
        background: '#16a34a',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
  </NodeShell>
);

const AudienceNode = ({ data, selected }) => (
  <NodeShell title="Audiência" subtitle="CSV → variáveis" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>
      Faça upload e mapeie colunas no painel ao lado.
    </div>

    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{
        background: '#6b7280',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{
        background: '#16a34a',
        width: PIN_SIZE,
        height: PIN_SIZE,
        border: '2px solid #fff',
      }}
    />
  </NodeShell>
);

// ---------- main page ----------
export default function JourneyBuilder() {
  // nodes/edges state
  const [nodes, setNodes, onNodesChange] = useNodesState([
    {
      id: 'start',
      type: 'start',
      position: { x: 140, y: 140 },
      data: {},
      selectable: true,
    },
    {
      id: 'end',
      type: 'end',
      position: { x: 860, y: 140 },
      data: {},
      selectable: true,
    },
  ]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  // selection
  const [selectedId, setSelectedId] = useState(null);
  const selectedNode = useMemo(
    () => nodes.find((n) => n.id === selectedId),
    [nodes, selectedId]
  );

  // templates (Twilio)
  const [templates, setTemplates] = useState([]);
  useEffect(() => {
    // tenta buscar templates da sua rota existente
    fetch('/api/templates/list')
      .then((r) => r.json())
      .then((json) => setTemplates(json.contents || []))
      .catch(() => setTemplates([]));
  }, []);

  // updates centralizados
  const patchNode = useCallback((id, patch) => {
    setNodes((nds) =>
      nds.map((n) => (n.id === id ? { ...n, data: { ...n.data, ...patch } } : n))
    );
  }, [setNodes]);

  // apenas 1 saída por nó (substitui se já existe)
  const onConnect = useCallback((params) => {
    setEdges((eds) => {
      const filtered = eds.filter((e) => e.source !== params.source);
      return addEdge(
        {
          ...params,
          type: 'smoothstep',
          style: { stroke: '#0f172a' },
          markerEnd: { type: MarkerType.ArrowClosed, color: '#0f172a' },
        },
        filtered
      );
    });
  }, [setEdges]);

  const onSelectionChange = useCallback(({ nodes: ns }) => {
    setSelectedId(ns?.[0]?.id ?? null);
  }, []);

  // toolbar actions
  const addMessageNode = () => {
    const id = uid('msg');
    setNodes((nds) => [
      ...nds,
      {
        id,
        type: 'message',
        position: { x: 360, y: 240 },
        data: { label: 'Mensagem', channel: 'whatsapp', mode: 'text', text: '' },
      },
    ]);
  };

  const addApiNode = () => {
    const id = uid('api');
    setNodes((nds) => [
      ...nds,
      {
        id,
        type: 'api',
        position: { x: 480, y: 340 },
        data: {
          method: 'GET',
          url: '',
          headers: '{}',
          body: '{}',
          mappings: [], // [{path:'data.user.id', var:'user_id'}]
          lastResponse: '',
        },
      },
    ]);
  };

  const addCodeNode = () => {
    const id = uid('code');
    setNodes((nds) => [
      ...nds,
      {
        id,
        type: 'code',
        position: { x: 560, y: 200 },
        data: {
          label: 'Code',
          code: '// return { nova_variavel: 123 };',
        },
      },
    ]);
  };

  const addAudienceNode = () => {
    const id = uid('aud');
    setNodes((nds) => [
      ...nds,
      {
        id,
        type: 'audience',
        position: { x: 320, y: 420 },
        data: { headers: [], rows: [], mapping: {}, phoneKey: '' },
      },
    ]);
  };

  const duplicateSelected = () => {
    if (!selectedNode) return;
    const id = uid(selectedNode.type);
    setNodes((nds) => [
      ...nds,
      {
        ...selectedNode,
        id,
        position: {
          x: selectedNode.position.x + 40,
          y: selectedNode.position.y + 40,
        },
        selected: false,
      },
    ]);
  };

  const deleteSelected = () => {
    if (!selectedNode) return;
    setEdges((eds) => eds.filter((e) => e.source !== selectedNode.id && e.target !== selectedNode.id));
    setNodes((nds) => nds.filter((n) => n.id !== selectedNode.id));
    setSelectedId(null);
  };

  // salvar flow (backend ou localStorage)
  const handleSaveFlow = async () => {
    const payload = {
      id: 'flow_' + Date.now(),
      name: 'Novo Flow',
      nodes,
      edges,
      updatedAt: new Date().toISOString(),
    };
    try {
      const res = await fetch('/api/journeys/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });
      if (!res.ok) throw new Error('no backend');
      alert('Flow salvo no backend!');
    } catch {
      localStorage.setItem(payload.id, JSON.stringify(payload));
      alert('Flow salvo no navegador.');
    }
  };

  // node types
  const nodeTypes = useMemo(
    () => ({
      start: StartNode,
      end: EndNode,
      message: MessageNode,
      api: ApiNode,
      code: CodeNode,
      audience: AudienceNode,
    }),
    []
  );

  // ---------- properties panel ----------
  const TemplatesOptions = ({ value, onChange }) => (
    <select
      value={value || ''}
      onChange={(e) => onChange(e.target.value)}
      style={inputStyle}
    >
      <option value="">— none —</option>
      {templates.map((t) => (
        <option key={t.sid} value={t.sid}>
          {t.friendly_name || t.sid}
        </option>
      ))}
    </select>
  );

  const inputStyle = {
    width: '100%',
    border: '1px solid #cbd5e1',
    borderRadius: 8,
    padding: '10px 12px',
  };

  const small = { fontSize: 12, color: '#475569', marginBottom: 6 };
  const label = (t) => <div style={small}>{t}</div>;

  const PropertiesPanel = () => {
    if (!selectedNode) {
      return (
        <div style={panelWrap}>
          <div style={{ color: '#94a3b8' }}>Selecione um nó.</div>
        </div>
      );
    }

    const n = selectedNode;

    // MESSAGE
    if (n.type === 'message') {
      const d = n.data || {};
      return (
        <div style={panelWrap}>
          {label('Label')}
          <input
            style={inputStyle}
            value={d.label || ''}
            onChange={(e) => patchNode(n.id, { label: e.target.value })}
          />

          {label('Canal')}
          <select
            style={inputStyle}
            value={d.channel || 'whatsapp'}
            onChange={(e) => patchNode(n.id, { channel: e.target.value })}
          >
            <option value="whatsapp">WhatsApp</option>
            <option value="sms">SMS</option>
          </select>

          {label('Mode')}
          <select
            style={inputStyle}
            value={d.mode || 'text'}
            onChange={(e) => patchNode(n.id, { mode: e.target.value })}
          >
            <option value="text">Text</option>
            <option value="rich">Rich (text + image + buttons)</option>
          </select>

          {label('Pick a Twilio template (optional)')}
          <TemplatesOptions
            value={d.templateSid}
            onChange={(val) => patchNode(n.id, { templateSid: val })}
          />

          {label('Text')}
          <textarea
            rows={8}
            style={inputStyle}
            value={d.text || ''}
            onChange={(e) => patchNode(n.id, { text: e.target.value })}
            placeholder="Olá {{nome}}!"
          />

          {d.mode === 'rich' && (
            <>
              {label('Media URL')}
              <input
                style={inputStyle}
                value={d.mediaUrl || ''}
                onChange={(e) => patchNode(n.id, { mediaUrl: e.target.value })}
                placeholder="https://..."
              />

              {label('Buttons (JSON opcional)')}
              <textarea
                rows={6}
                style={inputStyle}
                value={d.buttonsJson || ''}
                onChange={(e) => patchNode(n.id, { buttonsJson: e.target.value })}
                placeholder='[{"type":"reply","title":"Sim","id":"YES"}]'
              />
            </>
          )}

          <div style={{ fontSize: 12, color: '#64748b', marginTop: 8 }}>
            Dica: use variáveis como <code>{'{{nome}}'}</code>.
          </div>
        </div>
      );
    }

    // API
    if (n.type === 'api') {
      const d = n.data || {};
      const tryParse = (txt) => {
        try {
          return JSON.stringify(JSON.parse(txt), null, 2);
        } catch {
          return txt;
        }
      };

      const testRequest = async () => {
        try {
          const headers = d.headers ? JSON.parse(d.headers) : {};
          const body =
            d.method === 'GET' || d.method === 'DELETE'
              ? undefined
              : d.body && d.body.trim() !== '{}'
              ? JSON.parse(d.body)
              : undefined;

          const res = await fetch(d.url, {
            method: d.method || 'GET',
            headers,
            body: body ? JSON.stringify(body) : undefined,
          });
          const text = await res.text();
          patchNode(n.id, { lastResponse: text });
          alert('Request executada. Veja a resposta abaixo.');
        } catch (e) {
          alert('Falha ao executar request: ' + e.message);
        }
      };

      const addMapping = () => {
        const m = Array.isArray(d.mappings) ? d.mappings.slice() : [];
        m.push({ path: '', var: '' });
        patchNode(n.id, { mappings: m });
      };

      const updateMap = (idx, patch) => {
        const m = d.mappings.slice();
        m[idx] = { ...m[idx], ...patch };
        patchNode(n.id, { mappings: m });
      };

      const removeMap = (idx) => {
        const m = d.mappings.slice();
        m.splice(idx, 1);
        patchNode(n.id, { mappings: m });
      };

      return (
        <div style={panelWrap}>
          {label('Label')}
          <input
            style={inputStyle}
            value={d.label || 'API Call'}
            onChange={(e) => patchNode(n.id, { label: e.target.value })}
          />

          {label('Método')}
          <select
            style={inputStyle}
            value={d.method || 'GET'}
            onChange={(e) => patchNode(n.id, { method: e.target.value })}
          >
            <option>GET</option>
            <option>POST</option>
            <option>PUT</option>
            <option>PATCH</option>
            <option>DELETE</option>
          </select>

          {label('URL')}
          <input
            style={inputStyle}
            placeholder="https://api.meuservico.com/..."
            value={d.url || ''}
            onChange={(e) => patchNode(n.id, { url: e.target.value })}
          />

          {label('Headers (JSON)')}
          <textarea
            rows={4}
            style={inputStyle}
            value={d.headers || '{}'}
            onChange={(e) => patchNode(n.id, { headers: e.target.value })}
          />

          {(d.method === 'POST' ||
            d.method === 'PUT' ||
            d.method === 'PATCH') && (
            <>
              {label('Body (JSON)')}
              <textarea
                rows={6}
                style={inputStyle}
                value={d.body || '{}'}
                onChange={(e) => patchNode(n.id, { body: e.target.value })}
              />
            </>
          )}

          <button className="btn" onClick={testRequest} style={{ marginTop: 10 }}>
            Executar request
          </button>

          {label('Resposta (último teste)')}
          <textarea
            rows={8}
            style={{ ...inputStyle, fontFamily: 'monospace' }}
            readOnly
            value={tryParse(d.lastResponse || '')}
          />

          {label('Mapeamentos JSON → Variáveis')}
          {(d.mappings || []).map((m, i) => (
            <div key={i} style={{ display: 'grid', gridTemplateColumns: '1fr 1fr auto', gap: 8, marginBottom: 8 }}>
              <input
                style={inputStyle}
                placeholder="ex.: data.user.id"
                value={m.path}
                onChange={(e) => updateMap(i, { path: e.target.value })}
              />
              <input
                style={inputStyle}
                placeholder="ex.: user_id"
                value={m.var}
                onChange={(e) => updateMap(i, { var: slug(e.target.value) })}
              />
              <button className="btn danger" onClick={() => removeMap(i)}>x</button>
            </div>
          ))}
          <button className="btn" onClick={addMapping}>+ adicionar mapeamento</button>

          <div style={{ fontSize: 12, color: '#64748b', marginTop: 8 }}>
            Use paths tipo <code>data.user.id</code>. O executor gravará o valor em variáveis.
          </div>
        </div>
      );
    }

    // CODE
    if (n.type === 'code') {
      const d = n.data || {};
      return (
        <div style={panelWrap}>
          {label('Label')}
          <input
            style={inputStyle}
            value={d.label || 'Code'}
            onChange={(e) => patchNode(n.id, { label: e.target.value })}
          />

          {label('JavaScript')}
          <textarea
            rows={14}
            style={{ ...inputStyle, fontFamily: 'monospace' }}
            value={d.code || ''}
            onChange={(e) => patchNode(n.id, { code: e.target.value })}
            placeholder={`// Recebe "context" (variáveis atuais)\n// deve retornar um objeto com novas variáveis\nreturn { nova_variavel: 123 };`}
          />
        </div>
      );
    }

    // AUDIENCE
    if (n.type === 'audience') {
      const d = n.data || {};
      const [headers, setHeaders] = useState(d.headers || []);
      const [rows, setRows] = useState(d.rows || []);
      const [mapping, setMapping] = useState(d.mapping || {});
      const [phoneKey, setPhoneKey] = useState(d.phoneKey || '');

      useEffect(() => {
        setHeaders(d.headers || []);
        setRows(d.rows || []);
        setMapping(d.mapping || {});
        setPhoneKey(d.phoneKey || '');
      }, [d.headers, d.rows, d.mapping, d.phoneKey]);

      const onFile = (e) => {
        const f = e.target.files?.[0];
        if (!f) return;
        Papa.parse(f, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const hs = res.meta.fields || Object.keys(res.data[0] || {});
            const guess = {};
            hs.forEach((h) => (guess[h] = slug(h)));
            const pk =
              hs.find((h) => /phone|telefone|celular|numero|n[uú]mero/i.test(h)) ||
              '';
            setHeaders(hs);
            setRows(res.data.slice(0, 5));
            setMapping(guess);
            setPhoneKey(pk);
          },
        });
      };

      const saveMap = () => {
        if (!phoneKey) {
          alert('Escolha a coluna de telefone.');
          return;
        }
        patchNode(n.id, {
          headers,
          rows,
          mapping,
          phoneKey,
        });
        alert('Mapeamento salvo.');
      };

      return (
        <div style={panelWrap}>
          {label('Arquivo CSV')}
          <input type="file" accept=".csv" onChange={onFile} />

          {headers.length > 0 && (
            <>
              {label('Coluna de telefone')}
              <select
                style={inputStyle}
                value={phoneKey}
                onChange={(e) => setPhoneKey(e.target.value)}
              >
                <option value="">— selecione —</option>
                {headers.map((h) => (
                  <option key={h} value={h}>{h}</option>
                ))}
              </select>

              {label('Mapeamento (coluna → variável)')}
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 20px 1fr', gap: 8, maxHeight: 200, overflow: 'auto' }}>
                {headers.map((h) => (
                  <React.Fragment key={h}>
                    <div style={{ alignSelf: 'center', fontSize: 13 }}>{h}</div>
                    <div style={{ textAlign: 'center', color: '#94a3b8' }}>→</div>
                    <input
                      style={inputStyle}
                      value={mapping[h] || ''}
                      placeholder={slug(h)}
                      onChange={(e) =>
                        setMapping((m) => ({ ...m, [h]: slug(e.target.value) }))
                      }
                    />
                  </React.Fragment>
                ))}
              </div>

              <button className="btn primary" onClick={saveMap} style={{ marginTop: 10 }}>
                Salvar mapeamento
              </button>

              {rows.length > 0 && (
                <>
                  {label('Amostra (5 primeiras linhas)')}
                  <pre
                    style={{
                      background: '#f8fafc',
                      border: '1px solid #e2e8f0',
                      borderRadius: 8,
                      padding: 10,
                      fontSize: 12,
                      maxHeight: 160,
                      overflow: 'auto',
                    }}
                  >
                    {JSON.stringify(rows, null, 2)}
                  </pre>
                </>
              )}
              <div style={{ fontSize: 12, color: '#64748b', marginTop: 8 }}>
                Dica: após salvar, use as variáveis com <code>{'{{minha_variavel}}'}</code>.
              </div>
            </>
          )}
        </div>
      );
    }

    // START / END
    return (
      <div style={panelWrap}>
        <div style={{ color: '#64748b' }}>Este nó não possui propriedades.</div>
      </div>
    );
  };

  const panelWrap = {
    background: '#fff',
    border: '1px solid #e5e7eb',
    borderRadius: 12,
    padding: 16,
  };

  return (
    <div style={{ display: 'grid', gridTemplateColumns: '1fr 360px', gap: 16, height: 'calc(100vh - 80px)' }}>
      <div style={{ height: '100%', border: '1px solid #e5e7eb', borderRadius: 12 }}>
        <div style={{ display: 'flex', gap: 8, padding: 12 }}>
          <button className="btn" onClick={addMessageNode}>+ Mensagem</button>
          <button className="btn" onClick={addApiNode}>+ API</button>
          <button className="btn" onClick={addCodeNode}>+ Code</button>
          <button className="btn" onClick={addAudienceNode}>+ Audiência</button>
          <button className="btn primary" onClick={handleSaveFlow}>Salvar flow</button>
          <button className="btn" onClick={duplicateSelected}>Duplicar</button>
          <button className="btn danger" onClick={deleteSelected}>Delete selected</button>
        </div>

        <ReactFlow
          nodes={nodes}
          edges={edges}
          nodeTypes={nodeTypes}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          fitView
          onSelectionChange={onSelectionChange}
        >
          <MiniMap />
          <Controls />
          <Background gap={16} />
        </ReactFlow>
      </div>

      <PropertiesPanel />
    </div>
  );
}
