import React, { useCallback, useEffect, useState } from 'react';
import ReactFlow, { Background, Controls, MiniMap, addEdge } from 'reactflow';
import 'reactflow/dist/style.css';

const nodeDefaults = { style: { border: '1px solid #ddd', borderRadius: 8, padding: 8, background:'#fff' }};

function NodeEditor({ selectedNode, updateNodeData }) {
  if (!selectedNode) return <div className="preview">Selecione um nó</div>;
  const data = selectedNode.data || {};
  const set = (k, v) => updateNodeData(selectedNode.id, { ...data, [k]: v });

  if (data.type === 'send') {
    return (
      <div className="campaign-card">
        <h4>Send</h4>
        <label>Canal</label>
        <select value={data.channel||'whatsapp'} onChange={e=>set('channel', e.target.value)}>
          <option value="whatsapp">WhatsApp</option>
          <option value="sms">SMS</option>
        </select>
        <label>Conteúdo</label>
        <textarea rows={5} value={data.content||''} onChange={e=>set('content', e.target.value)} />
        <label>Next</label>
        <input value={data.next||''} onChange={e=>set('next', e.target.value)} />
      </div>
    );
  }

  if (data.type === 'wait') {
    return (
      <div className="campaign-card">
        <h4>Wait</h4>
        <label>Duration (ISO-8601, ex: P2D, PT3H, PT30M)</label>
        <input value={data.duration||''} onChange={e=>set('duration', e.target.value)} />
        <label>Next</label>
        <input value={data.next||''} onChange={e=>set('next', e.target.value)} />
      </div>
    );
  }

  if (data.type === 'wait_for_reply') {
    return (
      <div className="campaign-card">
        <h4>Wait for Reply</h4>
        <label>Timeout (ISO-8601)</label>
        <input value={data.timeout||''} onChange={e=>set('timeout', e.target.value)} />
        <label>Next (branch node)</label>
        <input value={data.next||''} onChange={e=>set('next', e.target.value)} />
      </div>
    );
  }

  if (data.type === 'branch') {
    const condStr = (data.conditions||[]).map(c=>`${c.label}:${c.expr}->${c.next}`).join('\n');
    const setCondStr = (str)=>{
      const list = str.split('\n').map(l=>l.trim()).filter(Boolean).map(l=>{
        const m = l.match(/^([^:]+):([^>]+)->(.+)$/);
        if(!m) return null;
        return { label: m[1].trim(), expr: m[2].trim(), next: m[3].trim() };
      }).filter(Boolean);
      set('conditions', list);
    };
    return (
      <div className="campaign-card">
        <h4>Branch</h4>
        <label>Conditions (uma por linha: label:expr->next)</label>
        <textarea rows={6} value={condStr} onChange={e=>setCondStr(e.target.value)} />
        <label>timeoutNext</label>
        <input value={data.timeoutNext||''} onChange={e=>set('timeoutNext', e.target.value)} />
        <label>fallbackNext</label>
        <input value={data.fallbackNext||''} onChange={e=>set('fallbackNext', e.target.value)} />
      </div>
    );
  }

  return (
    <div className="campaign-card">
      <h4>{data.type || 'node'} </h4>
      <p>Sem editor específico.</p>
    </div>
  );
}

export default function JourneyBuilder() {
  const [flows, setFlows] = useState([]);
  const [flowId, setFlowId] = useState('');
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [selectedNode, setSelectedNode] = useState(null);

  const loadAll = async ()=>{
    const res = await fetch('/api/flows-admin/all');
    const j = await res.json();
    setFlows(j.flows||[]);
    if ((j.flows||[]).length && !flowId) setFlowId(j.flows[0].id);
  };

  useEffect(()=>{ loadAll(); }, []);

  useEffect(()=>{
    const f = flows.find(x=>x.id===flowId);
    if (!f) return;
    const ns = (f.nodes||[]).map((n,i)=>({
      id: n.id,
      position: { x: 80 + (i%4)*220, y: 80 + Math.floor(i/4)*160 },
      data: { ...n },
      draggable: true,
      style: nodeDefaults.style
    }));
    setNodes(ns);
    setEdges([]);
  }, [flows, flowId]);

  const onNodesChange = useCallback((chs)=>setNodes((nds)=>nds.map(n=>{
    const posChange = chs.find(x=>x.id===n.id && x.type==='position');
    if (posChange) return { ...n, position: posChange.position };
    return n;
  })), []);

  const onConnect = useCallback((params)=>setEdges((eds)=>addEdge(params, eds)), []);

  const updateNodeData = (id, data) => {
    setNodes(ns=>ns.map(n=> n.id===id ? { ...n, data } : n));
  };

  const saveFlow = async ()=>{
    const current = flows.map(f=>{
      if (f.id!==flowId) return f;
      const engineNodes = nodes.map(n=>{
        const { id, data } = n;
        const { type, ...rest } = data;
        return { id, type: data.type, ...rest };
      });
      return { ...f, nodes: engineNodes };
    });
    const res = await fetch('/api/flows-admin/save', {
      method:'POST',
      headers:{'Content-Type':'application/json'},
      body: JSON.stringify({ flows: current })
    });
    const j = await res.json();
    if (!res.ok) return alert(j.error||'erro ao salvar');
    setFlows(current);
    alert('Fluxo salvo.');
  };

  return (
    <div className="page-content" style={{display:'grid', gridTemplateColumns:'2fr 1fr', gap:16}}>
      <div>
        <h2>Journey Builder (MVP)</h2>
        <label>Flow</label>
        <select value={flowId} onChange={e=>setFlowId(e.target.value)}>
          {flows.map(f=> <option key={f.id} value={f.id}>{f.name} ({f.id})</option>)}
        </select>
        <div style={{height: '68vh', border:'1px solid #e5e7eb', borderRadius:8, overflow:'hidden', marginTop:8}}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onConnect={onConnect}
            onNodeClick={(_, node)=>setSelectedNode(node)}
            fitView
          >
            <MiniMap />
            <Controls />
            <Background />
          </ReactFlow>
        </div>
        <button className="primary-btn" onClick={saveFlow} style={{marginTop:12}}>Salvar Fluxo</button>
      </div>

      <div>
        <h3>Propriedades do Nó</h3>
        <NodeEditor selectedNode={selectedNode} updateNodeData={updateNodeData} />
      </div>
    </div>
  );
}
