// frontend/src/pages/JourneyBuilder.jsx
import React, { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import ReactFlow, {
  Background,
  Controls,
  MiniMap,
  addEdge,
  useEdgesState,
  useNodesState,
  MarkerType,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import Papa from 'papaparse';
import { saveAs } from 'file-saver';

const PIN_SIZE = 14;
const DND_TYPE = 'application/reactflow';
const SNAP = [16, 16];

// ---------- helpers ----------
const uid = (p) => `${p}_${Date.now()}_${Math.floor(Math.random() * 9999)}`;
const slug = (s) =>
  String(s || '')
    .trim()
    .toLowerCase()
    .replace(/\s+/g, '_')
    .replace(/[^\w]/g, '_')
    .replace(/_+/g, '_');

const defaultDataForType = (type) => {
  switch (type) {
    case 'message':
      return { label: 'Mensagem', channel: 'whatsapp', mode: 'text', text: '' };
    case 'api':
      return { label: 'API Call', method: 'GET', url: '', headers: '{}', body: '{}', mappings: [], lastResponse: '' };
    case 'code':
      return { label: 'Code', code: '// return { nova_variavel: 123 };' };
    case 'audience':
      return { headers: [], rows: [], mapping: {}, phoneKey: '' };
    case 'delay':
      return { label: 'Delay', mode: 'relative', amount: 1, unit: 'hours', until: '', untilLocal: '' };
    default:
      return {};
  }
};

const NodeShell = ({ title, subtitle, selected, children }) => (
  <div
    style={{
      background: '#fff',
      border: selected ? '2px solid #2563eb' : '1px solid #e5e7eb',
      borderRadius: 12,
      width: 320,
      boxShadow: '0 2px 8px rgba(0,0,0,.06)',
    }}
  >
    <div style={{ padding: '12px 14px', borderBottom: '1px solid #f1f5f9' }}>
      <div style={{ fontWeight: 700, color: '#0f172a' }}>{title}</div>
      {subtitle ? (
        <div style={{ fontSize: 12, color: '#64748b' }}>{subtitle}</div>
      ) : null}
    </div>
    <div style={{ padding: 12 }}>{children}</div>
  </div>
);

// ---------- node components ----------
const StartNode = ({ selected }) => (
  <NodeShell title="Start" subtitle="início do fluxo" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>Arraste do pino verde para o próximo nó.</div>
    <Handle type="source" position={Position.Right} id="out"
      style={{ background: '#16a34a', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
  </NodeShell>
);

const EndNode = ({ selected }) => (
  <NodeShell title="End" subtitle="término do fluxo" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>Nó terminal. Só recebe conexões.</div>
    <Handle type="target" position={Position.Left} id="in"
      style={{ background: '#6b7280', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
  </NodeShell>
);

const MessageNode = ({ data, selected }) => (
  <NodeShell
    title={data.label || 'Mensagem'}
    subtitle={`Canal: ${String(data.channel || 'whatsapp').toUpperCase()} • Tipo: ${data.mode || 'text'}`}
    selected={selected}
  >
    <div style={{ fontSize: 12, color: '#475569' }}>Conteúdo configurável no painel ao lado.</div>
    <Handle type="target" position={Position.Left} id="in"
      style={{ background: '#6b7280', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
    <Handle type="source" position={Position.Right} id="out"
      style={{ background: '#16a34a', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
  </NodeShell>
);

const ApiNode = ({ selected }) => (
  <NodeShell title="API Call" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>
      Configure método/URL no painel ao lado. Salva variáveis via mapeamentos.
    </div>
    <Handle type="target" position={Position.Left} id="in"
      style={{ background: '#6b7280', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
    <Handle type="source" position={Position.Right} id="out"
      style={{ background: '#16a34a', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
  </NodeShell>
);

const CodeNode = ({ selected }) => (
  <NodeShell title="Code" subtitle="JS custom" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>Cole seu JS no painel ao lado. Deve retornar um objeto de variáveis.</div>
    <Handle type="target" position={Position.Left} id="in"
      style={{ background: '#6b7280', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
    <Handle type="source" position={Position.Right} id="out"
      style={{ background: '#16a34a', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
  </NodeShell>
);

const AudienceNode = ({ selected }) => (
  <NodeShell title="Audiência" subtitle="CSV → variáveis" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>Faça upload e mapeie colunas no painel ao lado.</div>
    <Handle type="target" position={Position.Left} id="in"
      style={{ background: '#6b7280', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
    <Handle type="source" position={Position.Right} id="out"
      style={{ background: '#16a34a', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
  </NodeShell>
);

const DelayNode = ({ data = {}, selected }) => {
  const subtitle =
    data.mode === 'until'
      ? `até ${data.until ? new Date(data.until).toLocaleString() : '—'}`
      : `+ ${(data.amount ?? 1)} ${data.unit || 'hours'}`;
  return (
    <NodeShell title="Delay" subtitle={subtitle} selected={selected}>
      <div style={{ fontSize: 12, color: '#475569' }}>Espera antes de seguir para o próximo nó.</div>
      <Handle type="target" position={Position.Left} id="in"
        style={{ background: '#6b7280', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
      <Handle type="source" position={Position.Right} id="out"
        style={{ background: '#16a34a', width: PIN_SIZE, height: PIN_SIZE, border: '2px solid #fff' }} />
    </NodeShell>
  );
};

// ---------- side panel widgets ----------
function AudiencePanel({ node, patchNode, inputStyle, label, slugFn }) {
  const d = node.data || {};
  const [headers, setHeaders] = useState(d.headers || []);
  const [rows, setRows] = useState(d.rows || []);
  const [mapping, setMapping] = useState(d.mapping || {});
  const [phoneKey, setPhoneKey] = useState(d.phoneKey || '');

  useEffect(() => {
    setHeaders(d.headers || []);
    setRows(d.rows || []);
    setMapping(d.mapping || {});
    setPhoneKey(d.phoneKey || '');
  }, [node.id]);

  const onFile = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    Papa.parse(f, {
      header: true,
      skipEmptyLines: true,
      complete: (res) => {
        const hs = res.meta.fields || Object.keys(res.data[0] || {});
        const guess = {};
        hs.forEach((h) => (guess[h] = slugFn(h)));
        const pk = hs.find((h) => /phone|telefone|celular|numero|n[uú]mero/i.test(h)) || '';
        setHeaders(hs);
        setRows(res.data.slice(0, 5));
        setMapping(guess);
        setPhoneKey(pk);
      },
    });
  };

  const saveMap = () => {
    if (!phoneKey) {
      alert('Escolha a coluna de telefone.');
      return;
    }
    patchNode(node.id, { headers, rows, mapping, phoneKey });
    alert('Mapeamento salvo.');
  };

  return (
    <>
      {label('Arquivo CSV')}
      <input type="file" accept=".csv" onChange={onFile} />

      {headers.length > 0 && (
        <>
          {label('Coluna de telefone')}
          <select style={inputStyle} value={phoneKey} onChange={(e) => setPhoneKey(e.target.value)}>
            <option value="">— selecione —</option>
            {headers.map((h) => <option key={h} value={h}>{h}</option>)}
          </select>

          {label('Mapeamento (coluna → variável)')}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 20px 1fr', gap: 8, maxHeight: 200, overflow: 'auto' }}>
            {headers.map((h) => (
              <React.Fragment key={h}>
                <div style={{ alignSelf: 'center', fontSize: 13 }}>{h}</div>
                <div style={{ textAlign: 'center', color: '#94a3b8' }}>→</div>
                <input
                  style={inputStyle}
                  value={mapping[h] || ''}
                  placeholder={slugFn(h)}
                  onChange={(e) => setMapping((m) => ({ ...m, [h]: slugFn(e.target.value) }))}
                />
              </React.Fragment>
            ))}
          </div>

          <button className="btn" onClick={saveMap} style={{ marginTop: 10 }}>Salvar mapeamento</button>

          {rows.length > 0 && (
            <>
              {label('Amostra (5 primeiras linhas)')}
              <pre style={{
                background: '#f8fafc', border: '1px solid #e2e8f0', borderRadius: 8,
                padding: 10, fontSize: 12, maxHeight: 160, overflow: 'auto',
              }}>
                {JSON.stringify(rows, null, 2)}
              </pre>
            </>
          )}
          <div style={{ fontSize: 12, color: '#64748b', marginTop: 8 }}>
            Dica: após salvar, use as variáveis com <code>{'{{minha_variavel}}'}</code>.
          </div>
        </>
      )}
    </>
  );
}

// ---------- main page ----------
export default function JourneyBuilder() {
  // nodes/edges
  const [nodes, setNodes, onNodesChange] = useNodesState([
    { id: 'start', type: 'start', position: { x: 140, y: 160 }, data: {}, selectable: true },
    { id: 'end', type: 'end', position: { x: 860, y: 160 }, data: {}, selectable: true },
  ]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  // flow id & name
  const [flowId, setFlowId] = useState(null);
  const [flowName, setFlowName] = useState('Novo Flow');

  useEffect(() => {
    const sp = new URLSearchParams(window.location.search);
    const id = sp.get('id');
    if (!id) return;
    setFlowId(id);

    fetch(`/api/journeys/${id}`)
      .then((r) => (r.ok ? r.json() : Promise.reject()))
      .then((f) => {
        if (Array.isArray(f.nodes) && Array.isArray(f.edges)) {
          setNodes(f.nodes);
          setEdges(f.edges);
          if (f.name) setFlowName(f.name);
        }
      })
      .catch(() => {
        const raw = localStorage.getItem(id);
        if (raw) {
          try {
            const f = JSON.parse(raw);
            if (Array.isArray(f.nodes) && Array.isArray(f.edges)) {
              setNodes(f.nodes);
              setEdges(f.edges);
              if (f.name) setFlowName(f.name);
            }
          } catch {}
        }
      });
  }, [setEdges, setNodes]);

  // selection & side panel
  const [selectedId, setSelectedId] = useState(null);
  const selectedNode = useMemo(() => nodes.find((n) => n.id === selectedId), [nodes, selectedId]);
  const showSide = Boolean(selectedNode);

  // templates Twilio
  const [templates, setTemplates] = useState([]);
  useEffect(() => {
    fetch('/api/templates/list')
      .then((r) => r.json())
      .then((data) => setTemplates(data.contents || []))
      .catch(() => setTemplates([]));
  }, []);

  // centralized patch
  const patchNode = useCallback((id, patch) => {
    setNodes((nds) => nds.map((n) => (n.id === id ? { ...n, data: { ...n.data, ...patch } } : n)));
  }, [setNodes]);

  // 1 saída por nó
  const onConnect = useCallback((params) => {
    setEdges((eds) => {
      const filtered = eds.filter((e) => e.source !== params.source);
      return addEdge({
        ...params,
        type: 'smoothstep',
        style: { stroke: '#0f172a' },
        markerEnd: { type: MarkerType.ArrowClosed, color: '#0f172a' },
      }, filtered);
    });
  }, [setEdges]);

  const onSelectionChange = useCallback(({ nodes: ns }) => {
    setSelectedId(ns?.[0]?.id ?? null);
  }, []);

  // adders (botões + DnD usa a mesma lógica)
  const addNodeOfType = (type, position = { x: 360, y: 240 }) => {
    const id = uid(type);
    setNodes((nds) => nds.concat({ id, type, position, data: defaultDataForType(type) }));
    // auto-conecta Start → primeiro filho se Start ainda não tem saída
    setEdges((eds) => {
      const hasStartOut = eds.some((e) => e.source === 'start');
      if (!hasStartOut && type !== 'end') {
        return addEdge({
          id: uid('e'),
          source: 'start',
          sourceHandle: 'out',
          target: id,
          type: 'smoothstep',
          style: { stroke: '#0f172a' },
          markerEnd: { type: MarkerType.ArrowClosed, color: '#0f172a' },
        }, eds);
      }
      return eds;
    });
  };
  const addMessageNode = () => addNodeOfType('message');
  const addApiNode = () => addNodeOfType('api');
  const addCodeNode = () => addNodeOfType('code');
  const addAudienceNode = () => addNodeOfType('audience');
  const addDelayNode = () => addNodeOfType('delay');

  const duplicateNode = (node) => {
    const id = uid(node.type);
    setNodes((nds) => nds.concat({
      ...node,
      id,
      position: { x: node.position.x + 40, y: node.position.y + 40 },
      selected: false,
    }));
  };

  const removeNode = (id) => {
    setEdges((eds) => eds.filter((e) => e.source !== id && e.target !== id));
    setNodes((nds) => nds.filter((n) => n.id !== id));
    if (selectedId === id) setSelectedId(null);
  };

  // salvar flow
  const handleSaveFlow = async () => {
    let name = flowName || 'Novo Flow';
    if (!flowId && (!name || name === 'Novo Flow')) {
      const entered = window.prompt('Dê um nome para este flow:', name);
      if (entered && entered.trim()) {
        name = entered.trim();
        setFlowName(name);
      }
    }
    const payload = { id: flowId || 'flow_' + Date.now(), name, nodes, edges, updatedAt: new Date().toISOString() };

    try {
      const url = flowId ? `/api/journeys/${flowId}` : '/api/journeys';
      const method = flowId ? 'PUT' : 'POST';
      const res = await fetch(url, { method, headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(payload) });
      if (!res.ok) throw new Error('no backend');
      const saved = await res.json();
      alert('Flow salvo no backend!');
      if (!flowId && saved?.id) {
        window.history.replaceState(null, '', `/journeys/builder?id=${saved.id}`);
        setFlowId(saved.id);
      }
    } catch {
      localStorage.setItem(payload.id, JSON.stringify(payload));
      alert('Flow salvo no navegador.');
      if (!flowId) {
        window.history.replaceState(null, '', `/journeys/builder?id=${payload.id}`);
        setFlowId(payload.id);
      }
    }
  };

  // import/export
  const fileRef = useRef(null);
  const onImportClick = () => fileRef.current?.click();
  const onImportFile = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    const reader = new FileReader();
    reader.onload = () => {
      try {
        const json = JSON.parse(String(reader.result || '{}'));
        if (Array.isArray(json.nodes) && Array.isArray(json.edges)) {
          setNodes(json.nodes);
          setEdges(json.edges);
          if (json.id) setFlowId(json.id);
          if (json.name) setFlowName(json.name);
          alert('Flow importado.');
        } else {
          alert('Arquivo inválido.');
        }
      } catch (err) {
        alert('Falha ao importar: ' + (err?.message || 'erro'));
      }
    };
    reader.readAsText(f);
    e.target.value = '';
  };

  const onExport = () => {
    const data = { id: flowId || 'flow_unsaved', name: flowName || 'Novo Flow', nodes, edges, exportedAt: new Date().toISOString() };
    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json;charset=utf-8' });
    saveAs(blob, `${slug(data.name || 'flow')}.json`);
  };

  // ---- Drag & Drop (palette → canvas) ----
  const wrapperRef = useRef(null);
  const [rfInstance, setRfInstance] = useState(null);

  const onDragStart = (event, nodeType) => {
    event.dataTransfer.setData(DND_TYPE, nodeType);
    event.dataTransfer.effectAllowed = 'move';
  };
  const onDragOver = useCallback((event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  }, []);
  const onDrop = useCallback((event) => {
    event.preventDefault();
    const type = event.dataTransfer.getData(DND_TYPE);
    if (!type || !rfInstance || !wrapperRef.current) return;
    const bounds = wrapperRef.current.getBoundingClientRect();
    const position = rfInstance.project({ x: event.clientX - bounds.left, y: event.clientY - bounds.top });
    addNodeOfType(type, position);
  }, [rfInstance]);

  // ---- Context Menu (right-click no nó) ----
  const [ctx, setCtx] = useState({ open: false, x: 0, y: 0, nodeId: null, nodeType: null });
  const onNodeContextMenu = (evt, node) => {
    evt.preventDefault();
    setSelectedId(node.id);
    setCtx({ open: true, x: evt.clientX, y: evt.clientY, nodeId: node.id, nodeType: node.type });
  };
  const closeCtx = () => setCtx({ open: false, x: 0, y: 0, nodeId: null, nodeType: null });

  const renameCtx = () => {
    const n = nodes.find((x) => x.id === ctx.nodeId);
    if (!n) return;
    const curr = n.data?.label || '';
    const name = window.prompt('Novo nome do nó:', curr);
    if (name !== null) patchNode(n.id, { label: name });
    closeCtx();
  };
  const duplicateCtx = () => {
    const n = nodes.find((x) => x.id === ctx.nodeId);
    if (n) duplicateNode(n);
    closeCtx();
  };
  const resetCtx = () => {
    const n = nodes.find((x) => x.id === ctx.nodeId);
    if (n) patchNode(n.id, defaultDataForType(n.type));
    closeCtx();
  };
  const deleteCtx = () => {
    if (ctx.nodeId) removeNode(ctx.nodeId);
    closeCtx();
  };

  // node types
  const nodeTypes = useMemo(() => ({
    start: StartNode,
    end: EndNode,
    message: MessageNode,
    api: ApiNode,
    code: CodeNode,
    audience: AudienceNode,
    delay: DelayNode,
  }), []);

  // ---------- side panel ----------
  const inputStyle = { width: '100%', border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' };
  const small = { fontSize: 12, color: '#475569', marginBottom: 6 };
  const label = (t) => <div style={small}>{t}</div>;
  const panelWrap = { background: '#fff', border: '1px solid #e5e7eb', borderRadius: 12, padding: 16 };

  const TemplatesOptions = ({ value, onChange }) => (
    <select value={value || ''} onChange={(e) => onChange(e.target.value)} style={inputStyle}>
      <option value="">— none —</option>
      {templates.map((t) => <option key={t.sid} value={t.sid}>{t.friendly_name || t.sid}</option>)}
    </select>
  );

  const PropertiesPanel = () => {
    if (!selectedNode) return null;
    const n = selectedNode;

    if (n.type === 'message') {
      const d = n.data || {};
      const found = d.templateSid ? templates.find((t) => t.sid === d.templateSid) : null;
      const firstTypeBody = found ? (Object.values(found.types || {})[0]?.body || '') : '';
      return (
        <div style={panelWrap}>
          {label('Label')}
          <input style={inputStyle} value={d.label || ''} onChange={(e) => patchNode(n.id, { label: e.target.value })} />

          {label('Canal')}
          <select style={inputStyle} value={d.channel || 'whatsapp'} onChange={(e) => patchNode(n.id, { channel: e.target.value })}>
            <option value="whatsapp">WhatsApp</option>
            <option value="sms">SMS</option>
          </select>

          {label('Mode')}
          <select style={inputStyle} value={d.mode || 'text'} onChange={(e) => patchNode(n.id, { mode: e.target.value })}>
            <option value="text">Text</option>
            <option value="rich">Rich (text + image + buttons)</option>
          </select>

          {label('Pick a Twilio template (optional)')}
          <TemplatesOptions value={d.templateSid} onChange={(val) => patchNode(n.id, { templateSid: val })} />

          {found && (
            <>
              {label('Template preview')}
              <textarea readOnly rows={6} style={{ ...inputStyle, fontFamily: 'monospace' }} value={firstTypeBody} />
            </>
          )}

          {label('Text')}
          <textarea rows={8} style={inputStyle} value={d.text || ''} onChange={(e) => patchNode(n.id, { text: e.target.value })} placeholder="Olá {{nome}}!" />

          {d.mode === 'rich' && (
            <>
              {label('Media URL')}
              <input style={inputStyle} value={d.mediaUrl || ''} onChange={(e) => patchNode(n.id, { mediaUrl: e.target.value })} placeholder="https://..." />

              {label('Buttons (JSON opcional)')}
              <textarea rows={6} style={inputStyle} value={d.buttonsJson || ''} onChange={(e) => patchNode(n.id, { buttonsJson: e.target.value })}
                placeholder='[{"type":"reply","title":"Sim","id":"YES"}]' />
            </>
          )}

          <div style={{ fontSize: 12, color: '#64748b', marginTop: 8 }}>
            Dica: use variáveis como <code>{'{{nome}}'}</code>.
          </div>
        </div>
      );
    }

    if (n.type === 'api') {
      const d = n.data || {};
      const tryParse = (txt) => { try { return JSON.stringify(JSON.parse(txt), null, 2); } catch { return txt; } };
      const testRequest = async () => {
        try {
          const headers = d.headers ? JSON.parse(d.headers) : {};
          const body =
            d.method === 'GET' || d.method === 'DELETE' ? undefined :
            d.body && d.body.trim() !== '{}' ? JSON.parse(d.body) : undefined;
          const res = await fetch(d.url, { method: d.method || 'GET', headers, body: body ? JSON.stringify(body) : undefined });
          const text = await res.text();
          patchNode(n.id, { lastResponse: text });
          alert('Request executada. Veja a resposta abaixo.');
        } catch (e) {
          alert('Falha ao executar request: ' + e.message);
        }
      };
      const addMapping = () => {
        const m = Array.isArray(d.mappings) ? d.mappings.slice() : [];
        m.push({ path: '', var: '' });
        patchNode(n.id, { mappings: m });
      };
      const updateMap = (idx, patch) => {
        const m = (d.mappings || []).slice();
        m[idx] = { ...m[idx], ...patch };
        patchNode(n.id, { mappings: m });
      };
      const removeMap = (idx) => {
        const m = (d.mappings || []).slice();
        m.splice(idx, 1);
        patchNode(n.id, { mappings: m });
      };

      return (
        <div style={panelWrap}>
          {label('Label')}
          <input style={inputStyle} value={d.label || 'API Call'} onChange={(e) => patchNode(n.id, { label: e.target.value })} />

          {label('Método')}
          <select style={inputStyle} value={d.method || 'GET'} onChange={(e) => patchNode(n.id, { method: e.target.value })}>
            <option>GET</option><option>POST</option><option>PUT</option><option>PATCH</option><option>DELETE</option>
          </select>

          {label('URL')}
          <input style={inputStyle} placeholder="https://api.meuservico.com/..." value={d.url || ''} onChange={(e) => patchNode(n.id, { url: e.target.value })} />

          {label('Headers (JSON)')}
          <textarea rows={4} style={inputStyle} value={d.headers || '{}'} onChange={(e) => patchNode(n.id, { headers: e.target.value })} />

          {(d.method === 'POST' || d.method === 'PUT' || d.method === 'PATCH') && (
            <>
              {label('Body (JSON)')}
              <textarea rows={6} style={inputStyle} value={d.body || '{}'} onChange={(e) => patchNode(n.id, { body: e.target.value })} />
            </>
          )}

          <button className="btn" onClick={testRequest} style={{ marginTop: 10 }}>Executar request</button>

          {label('Resposta (último teste)')}
          <textarea rows={8} style={{ ...inputStyle, fontFamily: 'monospace' }} readOnly value={tryParse(d.lastResponse || '')} />

          {label('Mapeamentos JSON → Variáveis')}
          {(d.mappings || []).map((m, i) => (
            <div key={i} style={{ display: 'grid', gridTemplateColumns: '1fr 1fr auto', gap: 8, marginBottom: 8 }}>
              <input style={inputStyle} placeholder="ex.: data.user.id" value={m.path} onChange={(e) => updateMap(i, { path: e.target.value })} />
              <input style={inputStyle} placeholder="ex.: user_id" value={m.var} onChange={(e) => updateMap(i, { var: slug(e.target.value) })} />
              <button className="btn danger" onClick={() => removeMap(i)}>x</button>
            </div>
          ))}
          <button className="btn" onClick={addMapping}>+ adicionar mapeamento</button>
        </div>
      );
    }

    if (n.type === 'code') {
      const d = n.data || {};
      return (
        <div style={panelWrap}>
          {label('Label')}
          <input style={inputStyle} value={d.label || 'Code'} onChange={(e) => patchNode(n.id, { label: e.target.value })} />
          {label('JavaScript')}
          <textarea
            rows={14}
            style={{ ...inputStyle, fontFamily: 'monospace' }}
            value={d.code || ''}
            onChange={(e) => patchNode(n.id, { code: e.target.value })}
            placeholder={`// Recebe "context" (variáveis atuais)\n// deve retornar um objeto com novas variáveis\nreturn { nova_variavel: 123 };`}
          />
        </div>
      );
    }

    if (n.type === 'audience') {
      return (
        <div style={panelWrap}>
          <AudiencePanel node={n} patchNode={patchNode} inputStyle={inputStyle} label={label} slugFn={slug} />
        </div>
      );
    }

    if (n.type === 'delay') {
      const d = n.data || {};
      return (
        <div style={panelWrap}>
          {label('Label')}
          <input style={inputStyle} value={d.label || 'Delay'} onChange={(e) => patchNode(n.id, { label: e.target.value })} />
          {label('Modo')}
          <select style={inputStyle} value={d.mode || 'relative'} onChange={(e) => patchNode(n.id, { mode: e.target.value })}>
            <option value="relative">Por período (X unidades)</option>
            <option value="until">Até data/hora</option>
          </select>

          {(d.mode || 'relative') === 'relative' ? (
            <>
              {label('Quantidade')}
              <input type="number" min="1" step="1" style={inputStyle}
                value={d.amount ?? 1}
                onChange={(e) => patchNode(n.id, { amount: Math.max(1, parseInt(e.target.value || '1', 10)) })} />
              {label('Unidade')}
              <select style={inputStyle} value={d.unit || 'hours'} onChange={(e) => patchNode(n.id, { unit: e.target.value })}>
                <option value="seconds">segundos</option>
                <option value="minutes">minutos</option>
                <option value="hours">horas</option>
                <option value="days">dias</option>
                <option value="weeks">semanas</option>
                <option value="months">meses</option>
              </select>
            </>
          ) : (
            <>
              {label('Executar após (data/hora)')}
              <input
                type="datetime-local"
                style={inputStyle}
                value={d.untilLocal || ''}
                onChange={(e) => {
                  const local = e.target.value;
                  let iso = '';
                  try { iso = new Date(local).toISOString(); } catch {}
                  patchNode(n.id, { untilLocal: local, until: iso });
                }}
              />
              <div style={{ fontSize: 12, color: '#64748b' }}>
                Será convertido para UTC ao salvar. ISO: <code>{d.until || '—'}</code>
              </div>
            </>
          )}
        </div>
      );
    }

    return null;
  };

  // UI styles
  const chip = { padding: '6px 10px', border: '1px solid #c7d2fe', background: '#eef2ff', color: '#1e3a8a',
                 borderRadius: 8, fontSize: 12, cursor: 'grab', userSelect: 'none' };

  return (
    <div style={{ height: 'calc(100vh - 80px)', display: 'grid', gridTemplateColumns: showSide ? '1fr 360px' : '1fr', gap: 16 }}>
      <div style={{ border: '1px solid #e5e7eb', borderRadius: 12, overflow: 'hidden', position: 'relative' }}>
        {/* header */}
        <div style={{ display: 'flex', gap: 8, padding: 12, alignItems: 'center', borderBottom: '1px solid #eef2f7', position: 'sticky', top: 0, background: '#fff', zIndex: 2 }}>
          <button className="btn" onClick={() => (window.location.href = '/journeys')}>← Voltar p/ lista</button>

          <button className="btn" onClick={addMessageNode}>+ Mensagem</button>
          <button className="btn" onClick={addApiNode}>+ API</button>
          <button className="btn" onClick={addCodeNode}>+ Code</button>
          <button className="btn" onClick={addAudienceNode}>+ Audiência</button>
          <button className="btn" onClick={addDelayNode}>+ Delay</button>

          <div style={{ marginLeft: 'auto', display: 'flex', gap: 8, alignItems: 'center' }}>
            <button className="btn" onClick={onImportClick}>Importar</button>
            <button className="btn" onClick={onExport}>Exportar</button>
            <button className="btn" onClick={handleSaveFlow}>Salvar flow</button>
          </div>
          <input ref={fileRef} type="file" accept="application/json" onChange={onImportFile} style={{ display: 'none' }} />
        </div>

        {/* palette (drag & drop) */}
        <div style={{ display: 'flex', alignItems: 'center', gap: 8, padding: '8px 12px', borderBottom: '1px dashed #eef2f7' }}>
          <span style={{ fontSize: 12, color: '#64748b', marginRight: 6 }}>Arraste para o canvas:</span>
          <div style={chip} draggable onDragStart={(e) => onDragStart(e, 'message')}>Mensagem</div>
          <div style={chip} draggable onDragStart={(e) => onDragStart(e, 'api')}>API</div>
          <div style={chip} draggable onDragStart={(e) => onDragStart(e, 'code')}>Code</div>
          <div style={chip} draggable onDragStart={(e) => onDragStart(e, 'audience')}>Audiência</div>
          <div style={chip} draggable onDragStart={(e) => onDragStart(e, 'delay')}>Delay</div>
        </div>

        {/* canvas */}
        <div ref={wrapperRef} style={{ height: 'calc(100% - 110px)' }}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            nodeTypes={nodeTypes}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onSelectionChange={onSelectionChange}
            onInit={setRfInstance}
            onDragOver={onDragOver}
            onDrop={onDrop}
            onNodeContextMenu={onNodeContextMenu}
            onPaneClick={() => { setSelectedId(null); closeCtx(); }}
            panOnScroll
            zoomOnDoubleClick={false}
            snapToGrid
            snapGrid={SNAP}
            fitView
          >
            <MiniMap />
            <Controls />
            <Background gap={16} />
          </ReactFlow>
        </div>

        {/* context menu */}
        {ctx.open && (
          <div
            style={{
              position: 'fixed',
              top: ctx.y,
              left: ctx.x,
              background: '#fff',
              border: '1px solid #e5e7eb',
              borderRadius: 8,
              boxShadow: '0 6px 20px rgba(0,0,0,.12)',
              zIndex: 50,
              minWidth: 160,
              overflow: 'hidden',
            }}
            onMouseLeave={closeCtx}
          >
            <MenuItem onClick={renameCtx}>Renomear nó</MenuItem>
            <MenuItem onClick={duplicateCtx}>Duplicar</MenuItem>
            <MenuItem onClick={resetCtx}>Redefinir</MenuItem>
            <MenuItem danger onClick={deleteCtx}>Apagar</MenuItem>
          </div>
        )}
      </div>

      {/* side panel só quando há seleção */}
      {showSide && (
        <div style={{ background: '#fff', border: '1px solid #e5e7eb', borderRadius: 12, overflow: 'auto' }}>
          <PropertiesPanel />
        </div>
      )}
    </div>
  );
}

// pequeno componente de item do menu
function MenuItem({ children, onClick, danger }) {
  return (
    <div
      onClick={onClick}
      style={{
        padding: '10px 12px',
        cursor: 'pointer',
        fontSize: 14,
        color: danger ? '#b91c1c' : '#0f172a',
        borderBottom: '1px solid #f3f4f6',
        userSelect: 'none',
      }}
      onMouseDown={(e) => e.preventDefault()}
    >
      {children}
    </div>
  );
}
