// frontend/src/pages/JourneysPage.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function isFlowLike(x) {
  return x && Array.isArray(x.nodes) && Array.isArray(x.edges);
}

export default function JourneysPage() {
  const nav = useNavigate();
  const [flows, setFlows] = useState([]);
  const [loading, setLoading] = useState(true);

  // Carrega do localStorage e (se existir) do backend
  useEffect(() => {
    const load = async () => {
      const byId = new Map();

      // localStorage
      for (let i = 0; i < localStorage.length; i++) {
        const k = localStorage.key(i);
        try {
          const raw = localStorage.getItem(k);
          const f = JSON.parse(raw || '{}');
          if (isFlowLike(f)) {
            const id = f.id || k;
            byId.set(id, {
              ...f,
              id,
              source: 'local',
              updatedAt: f.updatedAt || new Date().toISOString(),
            });
          }
        } catch {}
      }

      // backend opcional
      try {
        const r = await fetch('/api/journeys');
        if (r.ok) {
          const arr = await r.json();
          (Array.isArray(arr) ? arr : []).forEach((f) => {
            if (isFlowLike(f)) {
              byId.set(f.id, {
                ...f,
                source: 'server',
                updatedAt: f.updatedAt || new Date().toISOString(),
              });
            }
          });
        }
      } catch {
        // ignore – seguimos só com localStorage
      }

      const list = Array.from(byId.values()).sort(
        (a, b) => new Date(b.updatedAt) - new Date(a.updatedAt)
      );
      setFlows(list);
      setLoading(false);
    };
    load();
  }, []);

  const edit = (id) => nav(`/journeys/builder?id=${id}`);
  const createNew = () => nav('/journeys/builder');

  const duplicate = (flow) => {
    const id = `flow_${Date.now()}`;
    const copy = {
      ...flow,
      id,
      name: `${flow.name || 'Flow'} (copy)`,
      updatedAt: new Date().toISOString(),
    };
    localStorage.setItem(id, JSON.stringify(copy));
    setFlows((x) => [copy, ...x]);
    nav(`/journeys/builder?id=${id}`);
  };

  const remove = async (flow) => {
    if (!window.confirm('Apagar este flow?')) return;
    try {
      await fetch(`/api/journeys/${flow.id}`, { method: 'DELETE' });
    } catch {}
    localStorage.removeItem(flow.id);
    setFlows((x) => x.filter((f) => f.id !== flow.id));
  };

  const exportJson = (flow) => {
    const blob = new Blob([JSON.stringify(flow, null, 2)], {
      type: 'application/json',
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${flow.name || flow.id}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const importJson = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    const rd = new FileReader();
    rd.onload = () => {
      try {
        const flow = JSON.parse(String(rd.result || '{}'));
        if (!isFlowLike(flow)) throw new Error('invalid');
        const id = flow.id || `flow_${Date.now()}`;
        const obj = { ...flow, id, updatedAt: new Date().toISOString() };
        localStorage.setItem(id, JSON.stringify(obj));
        setFlows((x) => [obj, ...x]);
      } catch {
        alert('JSON inválido.');
      }
    };
    rd.readAsText(f);
    e.target.value = '';
  };

  return (
    <div className="page-content">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Journeys</h2>
        <div style={{ display: 'flex', gap: 8 }}>
          <button className="btn" onClick={createNew}>+ Novo flow</button>
          <label className="btn secondary" style={{ cursor: 'pointer' }}>
            Importar JSON
            <input type="file" accept="application/json" style={{ display: 'none' }} onChange={importJson} />
          </label>
        </div>
      </div>

      {loading ? (
        <div>Carregando…</div>
      ) : flows.length === 0 ? (
        <div style={{ color: '#6b7280' }}>
          Nenhum flow encontrado. Clique em <b>+ Novo flow</b> para criar.
        </div>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: 12 }}>
          <thead>
            <tr style={{ textAlign: 'left' }}>
              <th style={{ padding: '10px 8px', borderBottom: '1px solid #e5e7eb' }}>Nome</th>
              <th style={{ padding: '10px 8px', borderBottom: '1px solid #e5e7eb' }}>Atualizado</th>
              <th style={{ padding: '10px 8px', borderBottom: '1px solid #e5e7eb' }}>Origem</th>
              <th style={{ padding: '10px 8px', borderBottom: '1px solid #e5e7eb' }}>Nós</th>
              <th style={{ padding: '10px 8px', borderBottom: '1px solid #e5e7eb' }}>Ações</th>
            </tr>
          </thead>
          <tbody>
            {flows.map((f) => (
              <tr key={f.id}>
                <td style={{ padding: '10px 8px' }}>{f.name || f.id}</td>
                <td style={{ padding: '10px 8px' }}>
                  {new Date(f.updatedAt).toLocaleString()}
                </td>
                <td style={{ padding: '10px 8px', textTransform: 'capitalize' }}>
                  {f.source || 'local'}
                </td>
                <td style={{ padding: '10px 8px' }}>{f.nodes?.length ?? 0}</td>
                <td style={{ padding: '10px 8px', display: 'flex', gap: 8 }}>
                  <button className="btn" onClick={() => edit(f.id)}>Editar</button>
                  <button className="btn secondary" onClick={() => duplicate(f)}>Duplicar</button>
                  <button className="btn secondary" onClick={() => exportJson(f)}>Exportar</button>
                  <button className="btn danger" onClick={() => remove(f)}>Apagar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
