// frontend/src/pages/TemplateStudio.jsx
import React, { useEffect, useMemo, useRef, useState } from 'react';

const LANGS = [
  { code: 'pt_BR', label: 'Português (Brasil)' },
  { code: 'en_US', label: 'English (US)' },
  { code: 'es_ES', label: 'Español (ES)' },
];

const CATEGORIES = [
  { value: 'MARKETING', label: 'Marketing' },
  { value: 'UTILITY', label: 'Utility' },
  { value: 'AUTHENTICATION', label: 'Authentication' },
];

const HeaderTypes = [
  { value: 'none', label: 'Sem header' },
  { value: 'text', label: 'Texto' },
  { value: 'image', label: 'Imagem' },
  { value: 'video', label: 'Vídeo' },
  { value: 'document', label: 'Documento' },
];

const BTN_TYPES = [
  { value: 'reply', label: 'Quick Reply' },
  { value: 'url', label: 'Call to Action (URL)' },
  { value: 'phone', label: 'Call to Action (Telefone)' },
];

const DraftKey = 'template_studio_draft_v1';

function useDebounced(value, delay = 600) {
  const [v, setV] = useState(value);
  useEffect(() => {
    const t = setTimeout(() => setV(value), delay);
    return () => clearTimeout(t);
  }, [value, delay]);
  return v;
}

export default function TemplateStudio() {
  const [name, setName] = useState('');
  const [language, setLanguage] = useState(LANGS[0].code);
  const [category, setCategory] = useState(CATEGORIES[0].value);

  const [headerType, setHeaderType] = useState('none');
  const [headerText, setHeaderText] = useState('');
  const [body, setBody] = useState('');
  const [footer, setFooter] = useState('');

  const [buttons, setButtons] = useState([]);
  const [checking, setChecking] = useState(false);
  const [checkResult, setCheckResult] = useState(null); // {status:'PASS'|'WARN'|'FAIL', issues:[...]}
  const [submitting, setSubmitting] = useState(false);

  // carrega rascunho
  useEffect(() => {
    try {
      const raw = localStorage.getItem(DraftKey);
      if (!raw) return;
      const d = JSON.parse(raw);
      setName(d.name || '');
      setLanguage(d.language || LANGS[0].code);
      setCategory(d.category || CATEGORIES[0].value);
      setHeaderType(d.headerType || 'none');
      setHeaderText(d.headerText || '');
      setBody(d.body || '');
      setFooter(d.footer || '');
      setButtons(Array.isArray(d.buttons) ? d.buttons : []);
    } catch {}
  }, []);

  // salva rascunho
  useEffect(() => {
    const draft = { name, language, category, headerType, headerText, body, footer, buttons };
    localStorage.setItem(DraftKey, JSON.stringify(draft));
  }, [name, language, category, headerType, headerText, body, footer, buttons]);

  // payload "padronizado"
  const payload = useMemo(() => {
    return {
      name: (name || '').trim(),
      language,
      category,
      header: headerType === 'none'
        ? null
        : { type: headerType, text: headerType === 'text' ? headerText : null },
      body,
      footer,
      buttons, // [{type:'reply'|'url'|'phone', text, url?, phone?}]
    };
  }, [name, language, category, headerType, headerText, body, footer, buttons]);

  // checagem (debounced)
  const debouncedPayload = useDebounced(payload, 700);
  useEffect(() => {
    let cancelled = false;

    async function run() {
      setChecking(true);
      setCheckResult(null);
      try {
        // tenta /api/template-guard (POST). Se der 404, tenta /api/template-guard/check
        let res = await fetch('/api/template-guard', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(debouncedPayload),
        });
        if (res.status === 404) {
          res = await fetch('/api/template-guard/check', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(debouncedPayload),
          });
        }
        const j = await res.json().catch(() => ({}));

        // Formatos esperados:
        // { ok: true, status: 'PASS'|'WARN'|'FAIL', issues: [{message, field?}] }
        // ou só { status, issues }
        const status = (j.status || (j.ok ? 'PASS' : 'FAIL')).toUpperCase();
        const issues = Array.isArray(j.issues) ? j.issues : (j.messages || []);
        if (!cancelled) setCheckResult({ status, issues });
      } catch (e) {
        if (!cancelled) setCheckResult({ status: 'FAIL', issues: [{ message: 'Erro ao validar. ' + e.message }] });
      } finally {
        if (!cancelled) setChecking(false);
      }
    }

    // regras simples de “não verificar” se ainda está muito vazio
    if (!debouncedPayload.name || !debouncedPayload.body) {
      setCheckResult(null);
      setChecking(false);
      return;
    }

    run();
    return () => { cancelled = true; };
  }, [debouncedPayload]);

  const statusColor = (s) =>
    s === 'PASS' ? '#16a34a' : s === 'WARN' ? '#f59e0b' : '#dc2626';

  const canSubmit = useMemo(() => {
    if (!name || !language || !category || !body) return false;
    if (checking) return false;
    if (!checkResult) return false;
    if (checkResult.status === 'FAIL') return false;
    return true; // PASS ou WARN
  }, [name, language, category, body, checking, checkResult]);

  // Botões
  const addButton = (type) => {
    const base = { type, text: '' };
    if (type === 'url') base.url = 'https://';
    if (type === 'phone') base.phone = '';
    setButtons((bs) => bs.concat(base));
  };
  const updateButton = (idx, patch) => {
    setButtons((bs) => {
      const next = bs.slice();
      next[idx] = { ...next[idx], ...patch };
      return next;
    });
  };
  const removeButton = (idx) => {
    setButtons((bs) => bs.filter((_, i) => i !== idx));
  };

  const submitTemplate = async () => {
    if (!canSubmit) return;
    setSubmitting(true);
    try {
      // Esperando seu endpoint de criação
      // Ajuste se o seu arquivo templateRoutes expõe outra rota (ex.: /api/templates/create)
      const res = await fetch('/api/templates/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });
      const j = await res.json().catch(() => ({}));
      if (!res.ok) throw new Error(j.error || 'Falha ao criar template');

      alert('Template enviado para aprovação com sucesso!');
      // Limpa rascunho
      localStorage.removeItem(DraftKey);
    } catch (e) {
      alert('Erro ao criar template: ' + e.message);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div style={{ display: 'grid', gridTemplateColumns: '1.2fr 0.8fr', gap: 16, padding: 16 }}>
      {/* Coluna esquerda: Form */}
      <div style={{ background: '#fff', border: '1px solid #e5e7eb', borderRadius: 12, padding: 16 }}>
        <h2 style={{ marginTop: 0 }}>Template Studio</h2>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 160px 200px', gap: 8 }}>
          <div>
            <div style={{ fontSize: 12, color: '#64748b', marginBottom: 4 }}>Nome do Template</div>
            <input
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="ex.: promo_boas_vindas"
              style={{ width: '100%', border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' }}
            />
          </div>

          <div>
            <div style={{ fontSize: 12, color: '#64748b', marginBottom: 4 }}>Idioma</div>
            <select
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
              style={{ width: '100%', border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' }}
            >
              {LANGS.map((l) => (
                <option key={l.code} value={l.code}>{l.label}</option>
              ))}
            </select>
          </div>

          <div>
            <div style={{ fontSize: 12, color: '#64748b', marginBottom: 4 }}>Categoria</div>
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              style={{ width: '100%', border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' }}
            >
              {CATEGORIES.map((c) => (
                <option key={c.value} value={c.value}>{c.label}</option>
              ))}
            </select>
          </div>
        </div>

        <div style={{ marginTop: 12 }}>
          <div style={{ fontSize: 12, color: '#64748b', marginBottom: 4 }}>Header</div>
          <div style={{ display: 'grid', gridTemplateColumns: '200px 1fr', gap: 8 }}>
            <select
              value={headerType}
              onChange={(e) => setHeaderType(e.target.value)}
              style={{ width: '100%', border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' }}
            >
              {HeaderTypes.map((h) => (
                <option key={h.value} value={h.value}>{h.label}</option>
              ))}
            </select>
            {headerType === 'text' && (
              <input
                value={headerText}
                onChange={(e) => setHeaderText(e.target.value)}
                placeholder="Texto do header (opcional)"
                style={{ width: '100%', border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' }}
              />
            )}
            {['image', 'video', 'document'].includes(headerType) && (
              <div style={{ fontSize: 12, color: '#475569', alignSelf: 'center' }}>
                (Envio do arquivo é feito no disparo da mensagem usando a mídia do template)
              </div>
            )}
          </div>
        </div>

        <div style={{ marginTop: 12 }}>
          <div style={{ fontSize: 12, color: '#64748b', marginBottom: 4 }}>Body</div>
          <textarea
            rows={8}
            value={body}
            onChange={(e) => setBody(e.target.value)}
            placeholder="Escreva a mensagem. Suporte a placeholders {{1}}, {{2}}..."
            style={{ width: '100%', border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' }}
          />
        </div>

        <div style={{ marginTop: 12 }}>
          <div style={{ fontSize: 12, color: '#64748b', marginBottom: 4 }}>Footer (opcional)</div>
          <input
            value={footer}
            onChange={(e) => setFooter(e.target.value)}
            placeholder="Rodapé opcional"
            style={{ width: '100%', border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' }}
          />
        </div>

        <div style={{ marginTop: 12 }}>
          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <div style={{ fontSize: 12, color: '#64748b', marginBottom: 4 }}>Botões (opcional)</div>
            <div style={{ display: 'flex', gap: 8 }}>
              {BTN_TYPES.map((b) => (
                <button key={b.value} className="btn secondary" onClick={() => addButton(b.value)}>
                  + {b.label}
                </button>
              ))}
            </div>
          </div>

          {buttons.length > 0 && (
            <div style={{ marginTop: 8, display: 'grid', gap: 8 }}>
              {buttons.map((btn, idx) => (
                <div key={idx} style={{ display: 'grid', gridTemplateColumns: '160px 1fr 1fr auto', gap: 8, alignItems: 'center' }}>
                  <select
                    value={btn.type}
                    onChange={(e) => updateButton(idx, { type: e.target.value })}
                    style={{ border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' }}
                  >
                    {BTN_TYPES.map((b) => (
                      <option key={b.value} value={b.value}>{b.label}</option>
                    ))}
                  </select>
                  <input
                    value={btn.text || ''}
                    onChange={(e) => updateButton(idx, { text: e.target.value })}
                    placeholder="Texto do botão"
                    style={{ border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' }}
                  />
                  {btn.type === 'url' && (
                    <input
                      value={btn.url || ''}
                      onChange={(e) => updateButton(idx, { url: e.target.value })}
                      placeholder="https://..."
                      style={{ border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' }}
                    />
                  )}
                  {btn.type === 'phone' && (
                    <input
                      value={btn.phone || ''}
                      onChange={(e) => updateButton(idx, { phone: e.target.value })}
                      placeholder="+55..."
                      style={{ border: '1px solid #cbd5e1', borderRadius: 8, padding: '10px 12px' }}
                    />
                  )}
                  <button className="btn danger" onClick={() => removeButton(idx)}>x</button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Coluna direita: Compliance + Preview */}
      <div style={{ display: 'grid', gap: 16 }}>
        <div style={{ background: '#fff', border: '1px solid #e5e7eb', borderRadius: 12, padding: 16 }}>
          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <h3 style={{ margin: 0 }}>Compliance</h3>
            <div style={{ fontSize: 12, color: '#94a3b8' }}>
              {checking ? 'Validando...' : checkResult ? `Status: ` : 'Preencha o nome e o body para validar'}
              {checkResult && (
                <strong style={{ color: statusColor(checkResult.status), marginLeft: 4 }}>
                  {checkResult.status}
                </strong>
              )}
            </div>
          </div>

          {checkResult?.issues?.length > 0 && (
            <div style={{ marginTop: 8, background: '#f8fafc', border: '1px solid #e2e8f0', borderRadius: 8, padding: 12 }}>
              <div style={{ fontSize: 12, color: '#475569', marginBottom: 6 }}>Observações:</div>
              <ul style={{ margin: 0, paddingLeft: 18 }}>
                {checkResult.issues.map((it, i) => (
                  <li key={i} style={{ fontSize: 13 }}>
                    {it.message || it.reason || String(it)}
                    {it.field ? <em style={{ color: '#64748b' }}> (campo: {it.field})</em> : null}
                  </li>
                ))}
              </ul>
            </div>
          )}

          <div style={{ marginTop: 12, display: 'flex', gap: 8 }}>
            <button
              className="btn primary"
              disabled={!canSubmit || submitting}
              onClick={submitTemplate}
            >
              {submitting ? 'Enviando...' : 'Criar template'}
            </button>
            <button
              className="btn"
              onClick={() => {
                localStorage.removeItem(DraftKey);
                alert('Rascunho limpo.');
              }}
            >
              Limpar rascunho
            </button>
          </div>
        </div>

        <div style={{ background: '#fff', border: '1px solid #e5e7eb', borderRadius: 12, padding: 16 }}>
          <h3 style={{ marginTop: 0 }}>Preview rápido</h3>
          <div style={{ fontSize: 12, color: '#64748b', marginBottom: 6 }}>Header</div>
          <div style={{ border: '1px dashed #e2e8f0', borderRadius: 8, padding: 8, marginBottom: 8, minHeight: 36 }}>
            {headerType === 'text' ? headerText : headerType !== 'none' ? `(${headerType})` : <span style={{ color: '#94a3b8' }}>(sem header)</span>}
          </div>

          <div style={{ fontSize: 12, color: '#64748b', marginBottom: 6 }}>Body</div>
          <div style={{ whiteSpace: 'pre-wrap', border: '1px dashed #e2e8f0', borderRadius: 8, padding: 8, marginBottom: 8, minHeight: 100 }}>
            {body || <span style={{ color: '#94a3b8' }}>(vazio)</span>}
          </div>

          <div style={{ fontSize: 12, color: '#64748b', marginBottom: 6 }}>Footer</div>
          <div style={{ border: '1px dashed #e2e8f0', borderRadius: 8, padding: 8, marginBottom: 8, minHeight: 36 }}>
            {footer || <span style={{ color: '#94a3b8' }}>(sem footer)</span>}
          </div>

          {buttons.length > 0 && (
            <>
              <div style={{ fontSize: 12, color: '#64748b', marginBottom: 6 }}>Botões</div>
              <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
                {buttons.map((b, i) => (
                  <span key={i} style={{ border: '1px solid #cbd5e1', borderRadius: 999, padding: '6px 10px', fontSize: 12 }}>
                    {b.text || '(sem texto)'} {b.type === 'url' ? `→ ${b.url || ''}` : b.type === 'phone' ? `→ ${b.phone || ''}` : ''}
                  </span>
                ))}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
