// frontend/src/pages/JourneysPage.jsx
import React, { useEffect, useRef, useState } from 'react';
import { saveAs } from 'file-saver';

function pickLocalFlows() {
  const out = [];
  for (let i = 0; i < localStorage.length; i++) {
    const k = localStorage.key(i);
    if (!k) continue;
    try {
      const raw = localStorage.getItem(k);
      const obj = JSON.parse(raw || 'null');
      if (obj && Array.isArray(obj.nodes) && Array.isArray(obj.edges)) {
        out.push({
          id: obj.id || k,
          name: obj.name || k,
          updatedAt: obj.updatedAt || null,
          _raw: obj,
          _key: k,
        });
      }
    } catch {}
  }
  out.sort((a, b) => String(b.updatedAt || '').localeCompare(String(a.updatedAt || '')));
  return out;
}

export default function JourneysPage() {
  const [flows, setFlows] = useState([]);
  const [loading, setLoading] = useState(true);

  async function load() {
    setLoading(true);
    try {
      const r = await fetch('/api/journeys');
      if (r.ok) {
        const arr = await r.json();
        setFlows(arr || []);
        setLoading(false);
        return;
      }
      throw new Error('no backend');
    } catch {
      setFlows(pickLocalFlows());
      setLoading(false);
    }
  }

  useEffect(() => { load(); }, []);

  const openFlow = (id) => (window.location.href = `/journeys/builder?id=${id}`);

  const renameFlow = async (f) => {
    const name = window.prompt('Novo nome do flow:', f.name);
    if (!name) return;
    try {
      const r = await fetch(`/api/journeys/${f.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...f, name }),
      });
      if (!r.ok) throw new Error();
      await load();
      return;
    } catch {
      const raw = localStorage.getItem(f.id) || localStorage.getItem(f._key || f.id);
      if (raw) {
        try {
          const obj = JSON.parse(raw);
          obj.name = name;
          obj.updatedAt = new Date().toISOString();
          localStorage.setItem(obj.id || f.id, JSON.stringify(obj));
        } catch {}
      }
      setFlows(pickLocalFlows());
    }
  };

  const duplicateFlow = async (f) => {
    const newId = `flow_${Date.now()}`;
    const newObj = {
      ...(f._raw || { id: f.id, name: f.name, nodes: [], edges: [] }),
      id: newId,
      name: `${f.name} (copy)`,
      updatedAt: new Date().toISOString(),
    };
    try {
      const r = await fetch('/api/journeys', {
        method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(newObj),
      });
      if (!r.ok) throw new Error();
      await load(); openFlow(newId); return;
    } catch {
      localStorage.setItem(newId, JSON.stringify(newObj));
      setFlows(pickLocalFlows()); openFlow(newId);
    }
  };

  const deleteFlow = async (f) => {
    if (!window.confirm(`Apagar o flow "${f.name}"?`)) return;
    try {
      const r = await fetch(`/api/journeys/${f.id}`, { method: 'DELETE' });
      if (!r.ok) throw new Error();
      await load(); return;
    } catch {
      localStorage.removeItem(f.id);
      if (f._key && f._key !== f.id) localStorage.removeItem(f._key);
      setFlows(pickLocalFlows());
    }
  };

  // export (linha)
  const exportFlow = async (f) => {
    try {
      // tenta pegar do backend
      const r = await fetch(`/api/journeys/${f.id}`);
      if (r.ok) {
        const obj = await r.json();
        const blob = new Blob([JSON.stringify(obj, null, 2)], { type: 'application/json;charset=utf-8' });
        saveAs(blob, `${(obj.name || f.name || 'flow').replace(/\s+/g, '_')}.json`);
        return;
      }
      throw new Error();
    } catch {
      const obj = f._raw || { id: f.id, name: f.name, nodes: [], edges: [], updatedAt: f.updatedAt || null };
      const blob = new Blob([JSON.stringify(obj, null, 2)], { type: 'application/json;charset=utf-8' });
      saveAs(blob, `${(obj.name || 'flow').replace(/\s+/g, '_')}.json`);
    }
  };

  // import (global)
  const fileRef = useRef(null);
  const triggerImport = () => fileRef.current?.click();
  const handleImport = async (e) => {
    const f = e.target.files?.[0]; e.target.value = '';
    if (!f) return;
    try {
      const text = await f.text();
      const obj = JSON.parse(text);
      if (!Array.isArray(obj.nodes) || !Array.isArray(obj.edges)) throw new Error('arquivo inválido');
      const newId = obj.id || `flow_${Date.now()}`;
      const payload = { ...obj, id: newId, updatedAt: new Date().toISOString() };

      try {
        const r = await fetch('/api/journeys', {
          method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(payload),
        });
        if (!r.ok) throw new Error();
        await load(); openFlow(newId); return;
      } catch {
        localStorage.setItem(newId, JSON.stringify(payload));
        setFlows(pickLocalFlows()); openFlow(newId);
      }
    } catch (err) {
      alert('Falha ao importar: ' + err.message);
    }
  };

  const createNew = async () => {
    const name = window.prompt('Nome do novo flow:', 'Novo Flow');
    const id = `flow_${Date.now()}`;
    const obj = {
      id,
      name: name || 'Novo Flow',
      nodes: [
        { id: 'start', type: 'start', position: { x: 140, y: 140 }, data: {}, selectable: true },
        { id: 'end', type: 'end', position: { x: 860, y: 140 }, data: {}, selectable: true },
      ],
      edges: [],
      updatedAt: new Date().toISOString(),
    };

    try {
      const r = await fetch('/api/journeys', {
        method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(obj),
      });
      if (!r.ok) throw new Error();
      await load(); openFlow(id); return;
    } catch {
      localStorage.setItem(id, JSON.stringify(obj));
      setFlows(pickLocalFlows()); openFlow(id);
    }
  };

  return (
    <div className="page-content">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', gap: 8 }}>
        <h2>Journeys</h2>
        <div style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
          <input ref={fileRef} type="file" accept="application/json,.json" style={{ display: 'none' }} onChange={handleImport} />
          <button className="btn" onClick={triggerImport}>Importar Flow</button>
          <button className="btn" onClick={createNew}>+ Novo Flow</button>
        </div>
      </div>

      {loading ? (
        <div style={{ color: '#64748b' }}>Carregando…</div>
      ) : flows.length === 0 ? (
        <div style={{ color: '#64748b', marginTop: 12 }}>
          Nenhum flow encontrado. Clique em <b>+ Novo Flow</b> ou <b>Importar Flow</b>.
        </div>
      ) : (
        <div style={{ marginTop: 12 }}>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ textAlign: 'left', borderBottom: '1px solid #e5e7eb' }}>
                <th style={{ padding: 8 }}>Nome</th>
                <th style={{ padding: 8, width: 220 }}>Atualizado em</th>
                <th style={{ padding: 8, width: 520 }}>Ações</th>
              </tr>
            </thead>
            <tbody>
              {flows.map((f) => (
                <tr key={f.id} style={{ borderBottom: '1px solid #f1f5f9' }}>
                  <td style={{ padding: 8 }}>{f.name}</td>
                  <td style={{ padding: 8 }}>{f.updatedAt ? new Date(f.updatedAt).toLocaleString() : '—'}</td>
                  <td style={{ padding: 8 }}>
                    <button className="btn" onClick={() => openFlow(f.id)}>Abrir</button>{' '}
                    <button className="btn" onClick={() => exportFlow(f)}>Exportar</button>{' '}
                    <button className="btn secondary" onClick={() => renameFlow(f)}>Renomear</button>{' '}
                    <button className="btn" onClick={() => duplicateFlow(f)}>Duplicar</button>{' '}
                    <button className="btn danger" onClick={() => deleteFlow(f)}>Apagar</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
