// frontend/src/pages/JourneysPage.jsx
import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Helpers
const fmt = (iso) => {
  try {
    return new Date(iso).toLocaleString();
  } catch {
    return '';
  }
};
const slug = (s) =>
  String(s || '')
    .trim()
    .toLowerCase()
    .replace(/\s+/g, '_')
    .replace(/[^\w]/g, '_')
    .replace(/_+/g, '_');

const newId = () => `flow_${Date.now()}_${Math.floor(Math.random() * 9999)}`;

// Try backend first, then fallback to localStorage
async function listFromBackend() {
  try {
    const r = await fetch('/api/journeys');
    if (!r.ok) throw new Error('nope');
    const arr = await r.json(); // expected [{id,name,updatedAt,nodes,edges}]
    if (!Array.isArray(arr)) throw new Error('shape');
    return arr;
  } catch {
    return null;
  }
}

function listFromLocalStorage() {
  const out = [];
  for (let i = 0; i < localStorage.length; i++) {
    const k = localStorage.key(i);
    if (!k) continue;
    // salvamos flows como qualquer id (ex: flow_...); filtra por JSON com nodes/edges
    try {
      const v = JSON.parse(localStorage.getItem(k));
      if (v && Array.isArray(v.nodes) && Array.isArray(v.edges)) {
        out.push({
          id: v.id || k,
          name: v.name || 'Flow',
          updatedAt: v.updatedAt || new Date().toISOString(),
          nodes: v.nodes,
          edges: v.edges,
        });
      }
    } catch {}
  }
  // ordena por updatedAt desc
  out.sort((a, b) => (b.updatedAt || '').localeCompare(a.updatedAt || ''));
  return out;
}

async function saveToBackend(flow) {
  try {
    const url = flow.id ? `/api/journeys/${flow.id}` : '/api/journeys';
    const method = flow.id ? 'PUT' : 'POST';
    const r = await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(flow),
    });
    return r.ok;
  } catch {
    return false;
  }
}
function saveToLocalStorage(flow) {
  localStorage.setItem(flow.id, JSON.stringify(flow));
}

async function deleteFromBackend(id) {
  try {
    const r = await fetch(`/api/journeys/${id}`, { method: 'DELETE' });
    return r.ok;
  } catch {
    return false;
  }
}
function deleteFromLocalStorage(id) {
  localStorage.removeItem(id);
}

export default function JourneysPage() {
  const navigate = useNavigate();
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingId, setEditingId] = useState(null);
  const [editName, setEditName] = useState('');

  // load
  useEffect(() => {
    (async () => {
      setLoading(true);
      const bk = await listFromBackend();
      if (bk) {
        // backend fornece tudo — usa direto
        const sorted = [...bk].sort((a, b) =>
          (b.updatedAt || '').localeCompare(a.updatedAt || '')
        );
        setRows(sorted);
      } else {
        setRows(listFromLocalStorage());
      }
      setLoading(false);
    })();
  }, []);

  const hasRows = rows.length > 0;

  const open = (id) => navigate(`/journeys/builder?id=${id}`);

  const exportOne = (row) => {
    const blob = new Blob([JSON.stringify({ name: row.name, nodes: row.nodes, edges: row.edges }, null, 2)], { type: 'application/json' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = `${slug(row.name || 'flow')}.json`;
    a.click();
    URL.revokeObjectURL(a.href);
  };

  const importOne = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'application/json';
    input.onchange = (ev) => {
      const f = ev.target.files?.[0];
      if (!f) return;
      const r = new FileReader();
      r.onload = async () => {
        try {
          const data = JSON.parse(r.result);
          if (!Array.isArray(data.nodes) || !Array.isArray(data.edges)) throw new Error('invalid');
          const flow = {
            id: newId(),
            name: data.name || 'Flow importado',
            nodes: data.nodes,
            edges: data.edges,
            updatedAt: new Date().toISOString(),
          };
          const ok = await saveToBackend(flow);
          if (!ok) saveToLocalStorage(flow);
          setRows((cur) => [flow, ...cur]);
        } catch {
          alert('Arquivo inválido.');
        }
      };
      r.readAsText(f);
    };
    input.click();
  };

  const duplicate = async (row) => {
    const flow = {
      id: newId(),
      name: `${row.name} (cópia)`,
      nodes: row.nodes,
      edges: row.edges,
      updatedAt: new Date().toISOString(),
    };
    const ok = await saveToBackend(flow);
    if (!ok) saveToLocalStorage(flow);
    setRows((cur) => [flow, ...cur]);
  };

  const remove = async (row) => {
    const ok = window.confirm(`Apagar "${row.name}"?`);
    if (!ok) return;
    const okBk = await deleteFromBackend(row.id);
    if (!okBk) deleteFromLocalStorage(row.id);
    setRows((cur) => cur.filter((r) => r.id !== row.id));
  };

  const startRename = (row) => {
    setEditingId(row.id);
    setEditName(row.name || '');
  };
  const saveRename = async (row) => {
    const name = (editName || '').trim() || 'Sem nome';
    const updated = { ...row, name, updatedAt: new Date().toISOString() };
    const ok = await saveToBackend(updated);
    if (!ok) saveToLocalStorage(updated);
    setRows((cur) => cur.map((r) => (r.id === row.id ? updated : r)));
    setEditingId(null);
    setEditName('');
  };

  const table = useMemo(() => {
    if (!hasRows) return null;
    return (
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ textAlign: 'left', borderBottom: '1px solid #e5e7eb' }}>
            <th style={{ padding: '12px' }}>Nome</th>
            <th style={{ padding: '12px', width: 220 }}>Atualizado em</th>
            <th style={{ padding: '12px', width: 420 }}>Ações</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row) => (
            <tr key={row.id} style={{ borderBottom: '1px solid #f1f5f9' }}>
              <td style={{ padding: '12px' }}>
                {editingId === row.id ? (
                  <input
                    autoFocus
                    value={editName}
                    onChange={(e) => setEditName(e.target.value)}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter') saveRename(row);
                      if (e.key === 'Escape') {
                        setEditingId(null);
                        setEditName('');
                      }
                    }}
                    style={{
                      width: '100%',
                      border: '1px solid #cbd5e1',
                      borderRadius: 8,
                      padding: '8px 10px',
                    }}
                  />
                ) : (
                  <span style={{ fontWeight: 600 }}>{row.name}</span>
                )}
              </td>
              <td style={{ padding: '12px', color: '#475569' }}>{fmt(row.updatedAt)}</td>
              <td style={{ padding: '12px' }}>
                <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
                  <button className="btn" onClick={() => open(row.id)}>Abrir</button>
                  <button className="btn secondary" onClick={() => exportOne(row)}>Exportar</button>
                  {editingId === row.id ? (
                    <button className="btn" onClick={() => saveRename(row)}>Salvar nome</button>
                  ) : (
                    <button className="btn secondary" onClick={() => startRename(row)}>Renomear</button>
                  )}
                  <button className="btn secondary" onClick={() => duplicate(row)}>Duplicar</button>
                  <button className="btn danger" onClick={() => remove(row)}>Apagar</button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }, [rows, editingId, editName]);

  return (
    <div style={{ padding: '24px' }}>
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: 16 }}>
        <h2 style={{ margin: 0 }}>Journeys</h2>
        <div style={{ display: 'flex', gap: 8 }}>
          <button className="btn secondary" onClick={importOne}>Importar Flow</button>
          <button className="btn" onClick={() => navigate('/journeys/builder?new=1')}>+ Novo Flow</button>
        </div>
      </div>

      {loading ? (
        <div style={{ color: '#64748b' }}>Carregando…</div>
      ) : hasRows ? (
        <div className="campaign-grid">
          {table}
        </div>
      ) : (
        <div className="campaign-card">
          <div className="campaign-meta">
            Nenhum fluxo ainda. Você pode <button className="btn" onClick={() => navigate('/journeys/builder?new=1')}>criar um novo flow</button> ou
            &nbsp;<button className="btn secondary" onClick={importOne}>importar de um arquivo</button>.
          </div>
        </div>
      )}
    </div>
  );
}
