// frontend/src/pages/JourneysPage.jsx
import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';

// --- storage keys (compatível com o Builder que te passei)
const STORAGE_KEY = 'flows_v1';
const CURRENT_KEY = 'flows_current';

// --- sane helpers (iguais aos do Builder)
const sanitizeNodes = (list) =>
  (list || [])
    .filter(Boolean)
    .map((n, idx) => {
      const x = Number(n?.position?.x);
      const y = Number(n?.position?.y);
      return {
        id: String(n?.id ?? `n_${idx}`),
        type: n?.type || 'default',
        data: n?.data || { label: '' },
        position: Number.isFinite(x) && Number.isFinite(y) ? { x, y } : { x: 100 + idx * 40, y: 100 },
      };
    });

const sanitizeEdges = (list) =>
  (list || [])
    .filter(Boolean)
    .map((e, idx) => ({
      id: String(e?.id ?? `e_${idx}`),
      source: String(e?.source),
      target: String(e?.target),
      label: e?.label,
      animated: !!e?.animated,
    }));

function loadFlows() {
  try {
    const raw = localStorage.getItem(STORAGE_KEY);
    const arr = raw ? JSON.parse(raw) : [];
    if (!Array.isArray(arr)) return [];
    return arr.map((f) => ({
      ...f,
      nodes: sanitizeNodes(f.nodes),
      edges: sanitizeEdges(f.edges),
    }));
  } catch {
    return [];
  }
}

function saveFlows(flows) {
  const safe = (flows || []).map((f) => ({
    ...f,
    nodes: sanitizeNodes(f.nodes),
    edges: sanitizeEdges(f.edges),
  }));
  localStorage.setItem(STORAGE_KEY, JSON.stringify(safe));
  return safe;
}

function download(filename, text) {
  const blob = new Blob([text], { type: 'application/json;charset=utf-8' });
  const url = URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.href = url; a.download = filename; a.click();
  URL.revokeObjectURL(url);
}

export default function JourneysPage() {
  const navigate = useNavigate();
  const [flows, setFlows] = useState(() => loadFlows());
  const [editingId, setEditingId] = useState(null);
  const [editingValue, setEditingValue] = useState('');

  useEffect(() => {
    // mantém a lista sempre sanitizada
    const sanitized = loadFlows();
    setFlows(sanitized);
  }, []);

  const counts = useMemo(
    () =>
      Object.fromEntries(
        flows.map((f) => [f.id, { nodes: (f.nodes || []).length, edges: (f.edges || []).length }])
      ),
    [flows]
  );

  const createFlow = () => {
    const id = `flow_${Date.now()}`;
    const newFlow = {
      id,
      name: 'New Flow',
      nodes: sanitizeNodes([
        { id: 'start', type: 'input', position: { x: 80, y: 80 }, data: { label: 'Start' } },
        { id: 'end', type: 'output', position: { x: 360, y: 80 }, data: { label: 'End' } },
      ]),
      edges: sanitizeEdges([{ id: 'e1', source: 'start', target: 'end' }]),
    };
    const updated = saveFlows([...(flows || []), newFlow]);
    setFlows(updated);
    setEditingId(id);
    setEditingValue('New Flow');
  };

  const duplicateFlow = (id) => {
    const base = flows.find((f) => f.id === id);
    if (!base) return;
    const newId = `${base.id}_copy_${Date.now()}`;
    const clone = {
      ...base,
      id: newId,
      name: `${base.name} (copy)`,
      nodes: sanitizeNodes(base.nodes),
      edges: sanitizeEdges(base.edges),
    };
    const updated = saveFlows([...(flows || []), clone]);
    setFlows(updated);
  };

  const deleteFlow = (id) => {
    if (!confirm('Delete this flow?')) return;
    const updated = saveFlows(flows.filter((f) => f.id !== id));
    setFlows(updated);
    const current = localStorage.getItem(CURRENT_KEY);
    if (current === id) localStorage.removeItem(CURRENT_KEY);
  };

  const startEdit = (f) => {
    setEditingId(f.id);
    setEditingValue(f.name || '');
  };

  const commitEdit = () => {
    if (!editingId) return;
    const updated = saveFlows(
      flows.map((f) => (f.id === editingId ? { ...f, name: editingValue || f.name } : f))
    );
    setFlows(updated);
    setEditingId(null);
    setEditingValue('');
  };

  const exportAll = () => {
    download(`flows_export_${Date.now()}.json`, JSON.stringify(flows, null, 2));
  };

  const importFromFile = (file) => {
    const reader = new FileReader();
    reader.onload = () => {
      try {
        const json = JSON.parse(String(reader.result));
        const incoming = Array.isArray(json) ? json : [json];
        // evita colisão de ids
        const existingIds = new Set(flows.map((f) => f.id));
        const prepared = incoming.map((f) => {
          let id = String(f.id || `flow_${Date.now()}`);
          if (existingIds.has(id)) id = `${id}_${Date.now()}`;
          return { ...f, id, nodes: sanitizeNodes(f.nodes), edges: sanitizeEdges(f.edges) };
        });
        const updated = saveFlows([...(flows || []), ...prepared]);
        setFlows(updated);
        alert(`Imported ${prepared.length} flow(s).`);
      } catch (e) {
        alert('Invalid JSON file.');
      }
    };
    reader.readAsText(file);
  };

  const openInBuilder = (id) => {
    localStorage.setItem(CURRENT_KEY, id);
    navigate('/journeys/builder');
  };

  return (
    <div>
      <h2>Journeys</h2>

      <div style={{ display: 'flex', gap: 8, marginBottom: 16 }}>
        <button onClick={createFlow}>+ New Flow</button>
        <button onClick={exportAll}>Export All</button>
        <label style={{ display: 'inline-block' }}>
          <span className="sr-only">Import JSON</span>
          <input
            type="file"
            accept="application/json"
            onChange={(e) => e.target.files?.[0] && importFromFile(e.target.files[0])}
            style={{ display: 'inline-block' }}
          />
        </label>
      </div>

      {flows.length === 0 ? (
        <div style={{ color: '#6b7280' }}>No flows yet. Create your first one.</div>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ textAlign: 'left', borderBottom: '1px solid #e5e7eb' }}>
              <th style={{ padding: 8 }}>Name</th>
              <th style={{ padding: 8 }}>ID</th>
              <th style={{ padding: 8 }}>Nodes</th>
              <th style={{ padding: 8 }}>Edges</th>
              <th style={{ padding: 8 }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {flows.map((f) => (
              <tr key={f.id} style={{ borderBottom: '1px solid #f1f5f9' }}>
                <td style={{ padding: 8 }}>
                  {editingId === f.id ? (
                    <input
                      autoFocus
                      value={editingValue}
                      onChange={(e) => setEditingValue(e.target.value)}
                      onBlur={commitEdit}
                      onKeyDown={(e) => e.key === 'Enter' && commitEdit()}
                      style={{ width: 280, padding: 6 }}
                    />
                  ) : (
                    <span
                      style={{ cursor: 'text' }}
                      title="Click to rename"
                      onClick={() => startEdit(f)}
                    >
                      {f.name || '(untitled)'}
                    </span>
                  )}
                </td>
                <td style={{ padding: 8, fontFamily: 'monospace', fontSize: 12 }}>{f.id}</td>
                <td style={{ padding: 8 }}>{counts[f.id]?.nodes ?? 0}</td>
                <td style={{ padding: 8 }}>{counts[f.id]?.edges ?? 0}</td>
                <td style={{ padding: 8, display: 'flex', gap: 8 }}>
                  <button onClick={() => openInBuilder(f.id)}>Open</button>
                  <button onClick={() => duplicateFlow(f.id)}>Duplicate</button>
                  <button onClick={() => deleteFlow(f.id)} style={{ background: '#ef4444' }}>
                    Delete
                  </button>
                  <button
                    onClick={() =>
                      download(`${f.id}.json`, JSON.stringify(f, null, 2))
                    }
                  >
                    Export
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
