// frontend/src/pages/RunsPage.jsx
import React, { useEffect, useState } from 'react';

export default function RunsPage() {
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  const load = async () => {
    setLoading(true);
    try {
      const r = await fetch('/api/runs');
      const j = await r.json();
      setRows(j || []);
    } catch {
      setRows([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
    const t = setInterval(load, 3000); // atualiza a cada 3s
    return () => clearInterval(t);
  }, []);

  const stopRun = async (id) => {
    if (!window.confirm('Parar esta execução agora?')) return;
    await fetch(`/api/runs/${id}/stop`, { method: 'POST' });
    load();
  };

  const exportCsv = async (id) => {
    // exemplo simples: exporta os metadados do run
    const r = await fetch(`/api/runs/${id}`);
    const run = await r.json();
    const fields = ['id','journeyId','journeyName','status','total','processed','progress','createdAt','updatedAt'];
    const line = fields.join(',') + '\n' + fields.map(k => JSON.stringify(run[k] ?? '')).join(',');
    const blob = new Blob([line], { type: 'text/csv;charset=utf-8' });
    const a = document.createElement('a'); a.href = URL.createObjectURL(blob);
    a.download = `${(run.journeyName || 'run')}-${run.id}.csv`; a.click();
    URL.revokeObjectURL(a.href);
  };

  const badge = (s) => {
    const map = {
      queued: { bg: '#fde68a', fg: '#92400e', txt: 'Na fila' },
      running: { bg: '#bfdbfe', fg: '#1e40af', txt: 'Rodando' },
      stopped: { bg: '#e5e7eb', fg: '#111827', txt: 'Parado' },
      failed:  { bg: '#fecaca', fg: '#991b1b', txt: 'Falhou' },
      completed: { bg: '#bbf7d0', fg: '#065f46', txt: 'Concluído' },
    };
    const b = map[s] || map.queued;
    return <span style={{ background:b.bg, color:b.fg, padding:'2px 8px', borderRadius:12, fontSize:12 }}>{b.txt}</span>;
  };

  return (
    <div className="page-content">
      <h2>Execuções</h2>

      {loading && <div style={{fontSize:12,color:'#6b7280'}}>carregando…</div>}

      <div className="campaign-grid" style={{maxWidth:900}}>
        {rows.map(row => (
          <div key={row.id} className="campaign-card" style={{display:'grid', gridTemplateColumns:'1fr auto', gap:12, alignItems:'center'}}>
            <div>
              <div style={{fontWeight:700}}>{row.journeyName}</div>
              <div className="campaign-meta">
                <div>ID: {row.id}</div>
                <div>Status: {badge(row.status)} &nbsp; • Progresso: {row.progress || 0}% ({row.processed || 0}/{row.total || 0})</div>
                <div>Início: {row.createdAt} &nbsp; • &Uacute;ltima atualização: {row.updatedAt}</div>
                {row.lastError && <div style={{color:'#b91c1c'}}>Erro: {row.lastError}</div>}
              </div>
            </div>

            <div style={{display:'flex', gap:8}}>
              <button className="btn secondary" onClick={() => exportCsv(row.id)}>Exportar</button>
              {['queued','running'].includes(row.status) ? (
                <button className="btn danger" onClick={() => stopRun(row.id)}>Parar</button>
              ) : null}
              <button className="btn" onClick={load}>Atualizar</button>
            </div>
          </div>
        ))}

        {rows.length === 0 && !loading && (
          <div style={{color:'#6b7280'}}>Nenhuma execução ainda.</div>
        )}
      </div>
    </div>
  );
}
