import React, { useEffect, useState } from 'react';

export default function JourneysPage() {
  const [flows, setFlows] = useState([]);
  const [contactsJson, setContactsJson] = useState('[{"phone":"+5511999999999","nome":"Gustavo"}]');
  const [channel, setChannel] = useState('whatsapp');
  const [runs, setRuns] = useState([]);

  useEffect(()=>{ fetch('/api/flows').then(r=>r.json()).then(d=>setFlows(d.flows||[])); }, []);

  const start = async (flowId) => {
    let contacts=[];
    try { contacts = JSON.parse(contactsJson); } catch { return alert('JSON inválido'); }
    const res = await fetch(`/api/flows/${flowId}/start`, {
      method:'POST', headers:{'Content-Type':'application/json'},
      body: JSON.stringify({ contacts, channel })
    });
    const data = await res.json();
    if (!res.ok) return alert(data.error||'erro');
    alert(`Runs: ${data.runs.length}`);
  };

  const loadRuns = async (flowId) => {
    const r = await fetch(`/api/flows/${flowId}/runs`).then(x=>x.json());
    setRuns(r.runs||[]);
  };

  return (
    <div className="page-content" style={{maxWidth:900}}>
      <h2>Journeys (MVP)</h2>
      <label>Canal</label>
      <select value={channel} onChange={e=>setChannel(e.target.value)}>
        <option value="whatsapp">WhatsApp</option>
        <option value="sms">SMS</option>
      </select>

      <label>Contatos (JSON)</label>
      <textarea rows={6} value={contactsJson} onChange={e=>setContactsJson(e.target.value)} />

      {flows.map(f=>(
        <div key={f.id} className="campaign-card" style={{marginTop:12}}>
          <div className="campaign-meta"><strong>{f.name}</strong> ({f.id})</div>
          <button className="primary-btn small" onClick={()=>start(f.id)}>Start</button>
          <button className="primary-btn small" style={{marginLeft:8}} onClick={()=>loadRuns(f.id)}>Runs</button>
        </div>
      ))}

      {runs.length>0 && (
        <div style={{marginTop:16}}>
          <h3>Status</h3>
          <pre className="preview" style={{maxHeight:300, overflow:'auto'}}>{JSON.stringify(runs,null,2)}</pre>
        </div>
      )}
    </div>
  );
}
