// frontend/src/pages/JourneyBuilder.jsx
import React, { useCallback, useEffect, useMemo, useState } from 'react';
import ReactFlow, {
  Background,
  Controls,
  MiniMap,
  addEdge,
  useEdgesState,
  useNodesState,
  MarkerType,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import Papa from 'papaparse';

const PIN = 14;

// ---------------- helpers ----------------
const uid = (p) => `${p}_${Date.now()}_${Math.floor(Math.random() * 9999)}`;
const slug = (s) =>
  String(s || '')
    .trim()
    .toLowerCase()
    .replace(/\s+/g, '_')
    .replace(/[^\w]/g, '_')
    .replace(/_+/g, '_');

const makeDefaultFlow = () => {
  const audId = uid('aud');
  const endId = uid('end');
  return {
    nodes: [
      {
        id: audId,
        type: 'audience',
        position: { x: 140, y: 220 },
        data: { headers: [], rows: [], mapping: {}, phoneKey: '' },
        selectable: true,
      },
      {
        id: endId,
        type: 'end',
        position: { x: 760, y: 220 },
        data: {},
        selectable: true,
      },
    ],
    edges: [],
  };
};

// ---------------- shell ----------------
const Card = ({ title, subtitle, selected, children }) => (
  <div
    style={{
      background: '#fff',
      border: selected ? '2px solid #2563eb' : '1px solid #e5e7eb',
      borderRadius: 12,
      width: 340,
      boxShadow: '0 2px 8px rgba(0,0,0,.06)',
    }}
  >
    <div style={{ padding: '12px 14px', borderBottom: '1px solid #f1f5f9' }}>
      <div style={{ fontWeight: 800, color: '#0f172a' }}>{title}</div>
      {subtitle ? (
        <div style={{ fontSize: 12, color: '#64748b' }}>{subtitle}</div>
      ) : null}
    </div>
    <div style={{ padding: 12 }}>{children}</div>
  </div>
);

// ---------------- node components ----------------
const EndNode = ({ selected }) => (
  <Card title="End" subtitle="término do fluxo" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>Nó terminal. Só recebe conexões.</div>
    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{ background: '#6b7280', width: PIN, height: PIN, border: '2px solid #fff' }}
    />
  </Card>
);

const MessageNode = ({ data, selected }) => (
  <Card
    title={data.label || 'Mensagem'}
    subtitle={`Canal: ${String(data.channel || 'whatsapp').toUpperCase()} • Tipo: ${data.mode || 'text'}`}
    selected={selected}
  >
    <div style={{ fontSize: 12, color: '#475569' }}>Conteúdo configurável no painel ao lado.</div>
    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{ background: '#6b7280', width: PIN, height: PIN, border: '2px solid #fff' }}
    />
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{ background: '#16a34a', width: PIN, height: PIN, border: '2px solid #fff' }}
    />
  </Card>
);

const ApiNode = ({ selected }) => (
  <Card title="API Call" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>
      Configure método/URL no painel ao lado. Salva variáveis via mapeamentos.
    </div>
    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{ background: '#6b7280', width: PIN, height: PIN, border: '2px solid #fff' }}
    />
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{ background: '#16a34a', width: PIN, height: PIN, border: '2px solid #fff' }}
    />
  </Card>
);

const CodeNode = ({ selected }) => (
  <Card title="Code" subtitle="JS custom" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>
      Cole seu JS no painel ao lado. Deve retornar um objeto de variáveis.
    </div>
    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{ background: '#6b7280', width: PIN, height: PIN, border: '2px solid #fff' }}
    />
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{ background: '#16a34a', width: PIN, height: PIN, border: '2px solid #fff' }}
    />
  </Card>
);

const AudienceNode = ({ selected }) => (
  <Card title="Audiência" subtitle="CSV → variáveis" selected={selected}>
    <div style={{ fontSize: 12, color: '#475569' }}>
      Faça upload e mapeie colunas no painel (drawer) ao lado.
    </div>
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{ background: '#16a34a', width: PIN, height: PIN, border: '2px solid #fff' }}
    />
  </Card>
);

const DelayNode = ({ data, selected }) => (
  <Card
    title="Delay"
    subtitle={
      data.mode === 'until'
        ? `até ${data.until || '—'}`
        : `${data.amount || 0} ${data.unit || 'seconds'}`
    }
    selected={selected}
  >
    <div style={{ fontSize: 12, color: '#475569' }}>Espera antes de seguir para o próximo nó.</div>
    <Handle
      type="target"
      position={Position.Left}
      id="in"
      style={{ background: '#6b7280', width: PIN, height: PIN, border: '2px solid #fff' }}
    />
    <Handle
      type="source"
      position={Position.Right}
      id="out"
      style={{ background: '#16a34a', width: PIN, height: PIN, border: '2px solid #fff' }}
    />
  </Card>
);

// ---------------- side panels (podem usar hooks) ----------------
function AudiencePanel({ node, patchNode, inputStyle, label, slugFn }) {
  const d = node.data || {};
  const [headers, setHeaders] = useState(d.headers || []);
  const [rows, setRows] = useState(d.rows || []);
  const [mapping, setMapping] = useState(d.mapping || {});
  const [phoneKey, setPhoneKey] = useState(d.phoneKey || '');

  useEffect(() => {
    setHeaders(d.headers || []);
    setRows(d.rows || []);
    setMapping(d.mapping || {});
    setPhoneKey(d.phoneKey || '');
  }, [node.id]); // atualiza ao trocar de nó

  const onFile = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    Papa.parse(f, {
      header: true,
      skipEmptyLines: true,
      complete: (res) => {
        const hs = res.meta.fields || Object.keys(res.data[0] || {});
        const guess = {};
        hs.forEach((h) => (guess[h] = slugFn(h)));
        const pk =
          hs.find((h) => /phone|telefone|celular|numero|n[uú]mero/i.test(h)) || '';
        setHeaders(hs);
        setRows(res.data.slice(0, 5));
        setMapping(guess);
        setPhoneKey(pk);
      },
    });
  };

  const saveMap = () => {
    if (!phoneKey) {
      alert('Escolha a coluna de telefone.');
      return;
    }
    patchNode(node.id, { headers, rows, mapping, phoneKey });
    alert('Mapeamento salvo.');
  };

  return (
    <>
      {label('Arquivo CSV')}
      <input type="file" accept=".csv" onChange={onFile} />

      {headers.length > 0 && (
        <>
          {label('Coluna de telefone')}
          <select
            style={inputStyle}
            value={phoneKey}
            onChange={(e) => setPhoneKey(e.target.value)}
          >
            <option value="">— selecione —</option>
            {headers.map((h) => (
              <option key={h} value={h}>
                {h}
              </option>
            ))}
          </select>

          {label('Mapeamento (coluna → variável)')}
          <div
            style={{
              display: 'grid',
              gridTemplateColumns: '1fr 20px 1fr',
              gap: 8,
              maxHeight: 200,
              overflow: 'auto',
            }}
          >
            {headers.map((h) => (
              <React.Fragment key={h}>
                <div style={{ alignSelf: 'center', fontSize: 13 }}>{h}</div>
                <div style={{ textAlign: 'center', color: '#94a3b8' }}>→</div>
                <input
                  style={inputStyle}
                  value={mapping[h] || ''}
                  placeholder={slugFn(h)}
                  onChange={(e) =>
                    setMapping((m) => ({ ...m, [h]: slugFn(e.target.value) }))
                  }
                />
              </React.Fragment>
            ))}
          </div>

          <button className="btn" onClick={saveMap} style={{ marginTop: 10 }}>
            Salvar mapeamento
          </button>

          {rows.length > 0 && (
            <>
              {label('Amostra (5 primeiras linhas)')}
              <pre
                style={{
                  background: '#f8fafc',
                  border: '1px solid #e2e8f0',
                  borderRadius: 8,
                  padding: 10,
                  fontSize: 12,
                  maxHeight: 160,
                  overflow: 'auto',
                }}
              >
                {JSON.stringify(rows, null, 2)}
              </pre>
            </>
          )}
        </>
      )}
    </>
  );
}

// ---------------- main ----------------
export default function JourneyBuilder() {
  // estado base
  const def = makeDefaultFlow();
  const [nodes, setNodes, onNodesChange] = useNodesState(def.nodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(def.edges);

  const [rf, setRf] = useState(null);
  const [selectedId, setSelectedId] = useState(null);
  const selectedNode = useMemo(
    () => nodes.find((n) => n.id === selectedId),
    [nodes, selectedId]
  );

  const [flowId, setFlowId] = useState(null);
  const [flowName, setFlowName] = useState('Novo Flow');

  // templates Twilio (para preview/picking)
  const [templates, setTemplates] = useState([]);
  useEffect(() => {
    fetch('/api/templates/list')
      .then((r) => r.json())
      .then((data) => setTemplates(data.contents || []))
      .catch(() => setTemplates([]));
  }, []);

  // carregar por ?id=
  useEffect(() => {
    const sp = new URLSearchParams(window.location.search);
    const id = sp.get('id');

    if (!id) {
      const base = makeDefaultFlow();
      setNodes(base.nodes);
      setEdges(base.edges);
      return;
    }

    setFlowId(id);
    fetch(`/api/journeys/${id}`)
      .then((r) => (r.ok ? r.json() : Promise.reject()))
      .then((f) => {
        if (Array.isArray(f.nodes) && Array.isArray(f.edges) && f.nodes.length) {
          setNodes(f.nodes);
          setEdges(f.edges);
          if (f.name) setFlowName(f.name);
        } else {
          const base = makeDefaultFlow();
          setNodes(base.nodes);
          setEdges(base.edges);
        }
      })
      .catch(() => {
        const raw = localStorage.getItem(id);
        if (raw) {
          try {
            const f = JSON.parse(raw);
            if (Array.isArray(f.nodes) && Array.isArray(f.edges) && f.nodes.length) {
              setNodes(f.nodes);
              setEdges(f.edges);
              if (f.name) setFlowName(f.name);
              return;
            }
          } catch {}
        }
        const base = makeDefaultFlow();
        setNodes(base.nodes);
        setEdges(base.edges);
      });
  }, [setNodes, setEdges]);

  // fit/zoom agradável sempre que há nós
  useEffect(() => {
    if (!rf) return;
    if (!nodes || nodes.length === 0) return;
    requestAnimationFrame(() => {
      try {
        rf.fitView({ padding: 0.2, includeHiddenNodes: false });
        const vp = rf.getViewport();
        if (vp?.zoom > 1.1) rf.setViewport({ x: vp.x, y: vp.y, zoom: 0.9 });
        if (vp?.zoom < 0.3) rf.setViewport({ x: vp.x, y: vp.y, zoom: 0.5 });
      } catch {}
    });
  }, [rf, nodes]);

  // patch de dados do nó
  const patchNode = useCallback(
    (id, patch) => {
      setNodes((nds) =>
        nds.map((n) => (n.id === id ? { ...n, data: { ...n.data, ...patch } } : n))
      );
    },
    [setNodes]
  );

  // conexões: somente 1 saída por nó
  const onConnect = useCallback(
    (params) => {
      setEdges((eds) => {
        const filtered = eds.filter((e) => e.source !== params.source);
        return addEdge(
          {
            ...params,
            type: 'smoothstep',
            style: { stroke: '#0f172a' },
            markerEnd: { type: MarkerType.ArrowClosed, color: '#0f172a' },
          },
          filtered
        );
      });
    },
    [setEdges]
  );

  const onSelectionChange = useCallback(({ nodes: ns }) => {
    setSelectedId(ns?.[0]?.id ?? null);
  }, []);

  // --------- drag & drop palette ---------
  const onDragStart = (e, type) => {
    e.dataTransfer.setData('application/x-node-type', type);
    e.dataTransfer.effectAllowed = 'move';
  };

  const createNodeByType = (type, position) => {
    const id = uid(type);
    if (type === 'message')
      return {
        id,
        type: 'message',
        position,
        data: { label: 'Mensagem', channel: 'whatsapp', mode: 'text', text: '' },
      };
    if (type === 'api')
      return {
        id,
        type: 'api',
        position,
        data: {
          label: 'API Call',
          method: 'GET',
          url: '',
          headers: '{}',
          body: '{}',
          mappings: [],
          lastResponse: '',
        },
      };
    if (type === 'code')
      return {
        id,
        type: 'code',
        position,
        data: { label: 'Code', code: '// return { nova_variavel: 123 };' },
      };
    if (type === 'audience')
      return {
        id,
        type: 'audience',
        position,
        data: { headers: [], rows: [], mapping: {}, phoneKey: '' },
      };
    if (type === 'delay')
      return {
        id,
        type: 'delay',
        position,
        data: { mode: 'duration', amount: 30, unit: 'seconds', until: '' },
      };
    if (type === 'end')
      return { id, type: 'end', position, data: {} };
    return null;
  };

  const onDrop = useCallback(
    (e) => {
      e.preventDefault();
      const type = e.dataTransfer.getData('application/x-node-type');
      if (!rf || !type) return;
      const pos = rf.project({ x: e.clientX, y: e.clientY });
      const node = createNodeByType(type, pos);
      if (!node) return;
      setNodes((nds) => nds.concat(node));
    },
    [rf, setNodes]
  );

  const onDragOver = useCallback((e) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  }, []);

  // --------- context menu ----------
  const [ctxMenu, setCtxMenu] = useState(null);
  const onNodeContextMenu = (evt, node) => {
    evt.preventDefault();
    setCtxMenu({ x: evt.clientX, y: evt.clientY, id: node.id });
  };

  const ctxClose = () => setCtxMenu(null);
  const ctxDelete = () => {
    if (!ctxMenu) return;
    const id = ctxMenu.id;
    setEdges((eds) => eds.filter((e) => e.source !== id && e.target !== id));
    setNodes((nds) => nds.filter((n) => n.id !== id));
    setSelectedId(null);
    ctxClose();
  };
  const ctxDuplicate = () => {
    if (!ctxMenu) return;
    const n = nodes.find((x) => x.id === ctxMenu.id);
    if (!n) return;
    const copy = { ...n, id: uid(n.type), position: { x: n.position.x + 40, y: n.position.y + 40 } };
    setNodes((nds) => nds.concat(copy));
    ctxClose();
  };
  const ctxRename = () => {
    if (!ctxMenu) return;
    const n = nodes.find((x) => x.id === ctxMenu.id);
    if (!n) return;
    const val = prompt('Novo nome/label do nó:', n.data?.label || '');
    if (val != null) patchNode(n.id, { label: val });
    ctxClose();
  };
  const ctxReset = () => {
    if (!ctxMenu) return;
    const n = nodes.find((x) => x.id === ctxMenu.id);
    if (!n) return;
    const type = n.type;
    const fresh = createNodeByType(type, n.position);
    if (fresh) patchNode(n.id, fresh.data || {});
    ctxClose();
  };

  // --------- auto-layout simples (colunas) ----------
  const autoLayout = () => {
    // tenta achar a audiência como raiz
    const root = nodes.find((n) => n.type === 'audience') || nodes[0];
    if (!root) return;

    const outFrom = (nid) => edges.filter((e) => e.source === nid).map((e) => e.target);
    const visited = new Set();
    const levels = {};
    const q = [{ id: root.id, d: 0 }];
    while (q.length) {
      const { id, d } = q.shift();
      if (visited.has(id)) continue;
      visited.add(id);
      levels[id] = Math.min(levels[id] ?? Infinity, d);
      outFrom(id).forEach((t) => q.push({ id: t, d: d + 1 }));
    }

    const byLevel = {};
    Object.entries(levels).forEach(([id, lv]) => {
      byLevel[lv] = byLevel[lv] || [];
      byLevel[lv].push(id);
    });

    const X0 = 140;
    const Y0 = 160;
    const XG = 420;
    const YG = 180;

    const newPos = {};
    Object.keys(byLevel)
      .map((k) => Number(k))
      .sort((a, b) => a - b)
      .forEach((lv) => {
        const ids = byLevel[lv];
        ids.forEach((id, i) => {
          newPos[id] = { x: X0 + lv * XG, y: Y0 + i * YG };
        });
      });

    setNodes((nds) =>
      nds.map((n) => (newPos[n.id] ? { ...n, position: newPos[n.id] } : n))
    );
  };

  // --------- salvar / importar / exportar ----------
  const saveFlow = async () => {
    const id = flowId || 'flow_' + Date.now();
    const payload = {
      id,
      name: flowName || 'Sem nome',
      nodes,
      edges,
      updatedAt: new Date().toISOString(),
    };

    try {
      const url = flowId ? `/api/journeys/${flowId}` : '/api/journeys';
      const method = flowId ? 'PUT' : 'POST';
      const res = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });
      if (!res.ok) throw new Error('no backend');
      const saved = await res.json();
      alert('Flow salvo no backend!');
      if (!flowId && saved?.id) {
        setFlowId(saved.id);
        const urlObj = new URL(window.location.href);
        urlObj.searchParams.set('id', saved.id);
        window.history.replaceState(null, '', urlObj.toString());
      }
    } catch {
      localStorage.setItem(id, JSON.stringify(payload));
      alert('Flow salvo no navegador.');
      if (!flowId) {
        setFlowId(id);
        const urlObj = new URL(window.location.href);
        urlObj.searchParams.set('id', id);
        window.history.replaceState(null, '', urlObj.toString());
      }
    }
  };

  const exportFlow = () => {
    const blob = new Blob(
      [
        JSON.stringify(
          { id: flowId || 'temp', name: flowName, nodes, edges },
          null,
          2
        ),
      ],
      { type: 'application/json' }
    );
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = `${slug(flowName || 'flow')}.json`;
    a.click();
  };

  const importFlow = async (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    const txt = await f.text();
    try {
      const j = JSON.parse(txt);
      if (Array.isArray(j.nodes) && Array.isArray(j.edges)) {
        setNodes(j.nodes);
        setEdges(j.edges);
        if (j.name) setFlowName(j.name);
        if (j.id) {
          setFlowId(j.id);
          const urlObj = new URL(window.location.href);
          urlObj.searchParams.set('id', j.id);
          window.history.replaceState(null, '', urlObj.toString());
        }
      } else {
        alert('Arquivo inválido.');
      }
    } catch {
      alert('JSON inválido.');
    } finally {
      e.target.value = '';
    }
  };

  // --------- tipos de nó ---------
  const nodeTypes = useMemo(
    () => ({
      end: EndNode,
      message: MessageNode,
      api: ApiNode,
      code: CodeNode,
      audience: AudienceNode,
      delay: DelayNode,
    }),
    []
  );

  // --------- painel lateral (drawer) ---------
  const inputStyle = {
    width: '100%',
    border: '1px solid #cbd5e1',
    borderRadius: 8,
    padding: '10px 12px',
  };
  const small = { fontSize: 12, color: '#475569', marginBottom: 6 };
  const label = (t) => <div style={small}>{t}</div>;

  const TemplatesOptions = ({ value, onChange }) => (
    <select value={value || ''} onChange={(e) => onChange(e.target.value)} style={inputStyle}>
      <option value="">— none —</option>
      {templates.map((t) => (
        <option key={t.sid} value={t.sid}>
          {t.friendly_name || t.sid}
        </option>
      ))}
    </select>
  );

  const Drawer = () => {
    const open = !!selectedNode;
    const wrap = {
      position: 'fixed',
      top: 64,
      right: 16,
      width: 360,
      height: 'calc(100vh - 80px)',
      background: '#fff',
      border: '1px solid #e5e7eb',
      borderRadius: 12,
      boxShadow: '0 12px 24px rgba(0,0,0,.08)',
      transform: `translateX(${open ? 0 : 420}px)`,
      transition: 'transform .25s ease',
      padding: 16,
      zIndex: 50,
      overflow: 'auto',
    };

    if (!selectedNode) return <div style={wrap}><div style={{color:'#94a3b8'}}>Selecione um nó.</div></div>;

    const n = selectedNode;

    if (n.type === 'message') {
      const d = n.data || {};
      const found = d.templateSid ? templates.find((t) => t.sid === d.templateSid) : null;
      const firstTypeBody = found ? (Object.values(found.types || {})[0]?.body || '') : '';

      return (
        <div style={wrap}>
          {label('Label')}
          <input style={inputStyle} value={d.label || ''} onChange={(e) => patchNode(n.id, { label: e.target.value })} />

          {label('Canal')}
          <select style={inputStyle} value={d.channel || 'whatsapp'} onChange={(e) => patchNode(n.id, { channel: e.target.value })}>
            <option value="whatsapp">WhatsApp</option>
            <option value="sms">SMS</option>
          </select>

          {label('Mode')}
          <select style={inputStyle} value={d.mode || 'text'} onChange={(e) => patchNode(n.id, { mode: e.target.value })}>
            <option value="text">Text</option>
            <option value="rich">Rich (text + image + buttons)</option>
          </select>

          {label('Pick a Twilio template (optional)')}
          <TemplatesOptions value={d.templateSid} onChange={(val) => patchNode(n.id, { templateSid: val })} />

          {found && (
            <>
              {label('Template preview')}
              <textarea readOnly rows={6} style={{ ...inputStyle, fontFamily: 'monospace' }} value={firstTypeBody} />
            </>
          )}

          {label('Text')}
          <textarea rows={8} style={inputStyle} value={d.text || ''} onChange={(e) => patchNode(n.id, { text: e.target.value })} placeholder="Olá {{nome}}!" />

          {d.mode === 'rich' && (
            <>
              {label('Media URL')}
              <input style={inputStyle} value={d.mediaUrl || ''} onChange={(e) => patchNode(n.id, { mediaUrl: e.target.value })} placeholder="https://..." />

              {label('Buttons (JSON opcional)')}
              <textarea rows={6} style={inputStyle} value={d.buttonsJson || ''} onChange={(e) => patchNode(n.id, { buttonsJson: e.target.value })} placeholder='[{"type":"reply","title":"Sim","id":"YES"}]' />
            </>
          )}
        </div>
      );
    }

    if (n.type === 'api') {
      const d = n.data || {};
      const tryParse = (txt) => {
        try { return JSON.stringify(JSON.parse(txt), null, 2); } catch { return txt; }
      };

      const testRequest = async () => {
        try {
          const headers = d.headers ? JSON.parse(d.headers) : {};
          const body =
            d.method === 'GET' || d.method === 'DELETE'
              ? undefined
              : d.body && d.body.trim() !== '{}'
              ? JSON.parse(d.body)
              : undefined;

          const res = await fetch(d.url, {
            method: d.method || 'GET',
            headers,
            body: body ? JSON.stringify(body) : undefined,
          });
          const text = await res.text();
          patchNode(n.id, { lastResponse: text });
          alert('Request executada. Veja a resposta abaixo.');
        } catch (e) {
          alert('Falha ao executar request: ' + e.message);
        }
      };

      const addMapping = () => {
        const m = Array.isArray(d.mappings) ? d.mappings.slice() : [];
        m.push({ path: '', var: '' });
        patchNode(n.id, { mappings: m });
      };
      const updateMap = (idx, patch) => {
        const m = (d.mappings || []).slice();
        m[idx] = { ...m[idx], ...patch };
        patchNode(n.id, { mappings: m });
      };
      const removeMap = (idx) => {
        const m = (d.mappings || []).slice();
        m.splice(idx, 1);
        patchNode(n.id, { mappings: m });
      };

      return (
        <div style={wrap}>
          {label('Label')}
          <input style={inputStyle} value={d.label || 'API Call'} onChange={(e) => patchNode(n.id, { label: e.target.value })} />

          {label('Método')}
          <select style={inputStyle} value={d.method || 'GET'} onChange={(e) => patchNode(n.id, { method: e.target.value })}>
            <option>GET</option><option>POST</option><option>PUT</option><option>PATCH</option><option>DELETE</option>
          </select>

          {label('URL')}
          <input style={inputStyle} placeholder="https://api.meuservico.com/..." value={d.url || ''} onChange={(e) => patchNode(n.id, { url: e.target.value })} />

          {label('Headers (JSON)')}
          <textarea rows={4} style={inputStyle} value={d.headers || '{}'} onChange={(e) => patchNode(n.id, { headers: e.target.value })} />

          {(d.method === 'POST' || d.method === 'PUT' || d.method === 'PATCH') && (
            <>
              {label('Body (JSON)')}
              <textarea rows={6} style={inputStyle} value={d.body || '{}'} onChange={(e) => patchNode(n.id, { body: e.target.value })} />
            </>
          )}

          <button className="btn" onClick={testRequest} style={{ marginTop: 10 }}>Executar request</button>

          {label('Resposta (último teste)')}
          <textarea rows={8} style={{ ...inputStyle, fontFamily: 'monospace' }} readOnly value={tryParse(d.lastResponse || '')} />

          {label('Mapeamentos JSON → Variáveis')}
          {(d.mappings || []).map((m, i) => (
            <div key={i} style={{ display: 'grid', gridTemplateColumns: '1fr 1fr auto', gap: 8, marginBottom: 8 }}>
              <input style={inputStyle} placeholder="ex.: data.user.id" value={m.path} onChange={(e) => updateMap(i, { path: e.target.value })} />
              <input style={inputStyle} placeholder="ex.: user_id" value={m.var} onChange={(e) => updateMap(i, { var: slug(e.target.value) })} />
              <button className="btn danger" onClick={() => removeMap(i)}>x</button>
            </div>
          ))}
          <button className="btn" onClick={addMapping}>+ adicionar mapeamento</button>
        </div>
      );
    }

    if (n.type === 'code') {
      const d = n.data || {};
      return (
        <div style={wrap}>
          {label('Label')}
          <input style={inputStyle} value={d.label || 'Code'} onChange={(e) => patchNode(n.id, { label: e.target.value })} />

          {label('JavaScript')}
          <textarea
            rows={14}
            style={{ ...inputStyle, fontFamily: 'monospace' }}
            value={d.code || ''}
            onChange={(e) => patchNode(n.id, { code: e.target.value })}
            placeholder={`// Recebe "context" (variáveis atuais)\n// deve retornar um objeto com novas variáveis\nreturn { nova_variavel: 123 };`}
          />
        </div>
      );
    }

    if (n.type === 'audience') {
      return (
        <div style={wrap}>
          <AudiencePanel
            node={n}
            patchNode={patchNode}
            inputStyle={inputStyle}
            label={label}
            slugFn={slug}
          />
        </div>
      );
    }

    if (n.type === 'delay') {
      const d = n.data || {};
      return (
        <div style={wrap}>
          {label('Modo')}
          <select
            style={inputStyle}
            value={d.mode || 'duration'}
            onChange={(e) => patchNode(n.id, { mode: e.target.value })}
          >
            <option value="duration">Por duração</option>
            <option value="until">Até data/hora</option>
          </select>

          {d.mode === 'duration' ? (
            <>
              {label('Quantidade')}
              <input
                type="number"
                min={0}
                style={inputStyle}
                value={d.amount ?? 0}
                onChange={(e) => patchNode(n.id, { amount: Number(e.target.value) })}
              />
              {label('Unidade')}
              <select
                style={inputStyle}
                value={d.unit || 'seconds'}
                onChange={(e) => patchNode(n.id, { unit: e.target.value })}
              >
                <option value="seconds">seconds</option>
                <option value="minutes">minutes</option>
                <option value="hours">hours</option>
                <option value="days">days</option>
                <option value="weeks">weeks</option>
                <option value="months">months</option>
              </select>
            </>
          ) : (
            <>
              {label('Executar após (data/hora)')}
              <input
                type="datetime-local"
                style={inputStyle}
                value={d.until || ''}
                onChange={(e) => patchNode(n.id, { until: e.target.value })}
              />
            </>
          )}
        </div>
      );
    }

    return <div style={wrap}><div style={{ color: '#64748b' }}>Este nó não possui propriedades.</div></div>;
  };

  // --------- UI ---------
  const chip = {
    display: 'inline-block',
    background: '#eef2ff',
    color: '#1e3a8a',
    border: '1px solid #c7d2fe',
    borderRadius: 24,
    padding: '6px 12px',
    fontSize: 13,
    fontWeight: 700,
    cursor: 'grab',
    userSelect: 'none',
  };

  const topbar = {
    display: 'flex',
    alignItems: 'center',
    gap: 8,
    padding: '10px 12px',
  };

  return (
    <div style={{ height: '100vh', display: 'grid', gridTemplateRows: '56px 1fr' }}>
      {/* Top bar */}
      <div style={topbar}>
        <button className="btn" onClick={() => (window.location.href = '/journeys')}>
          ← Voltar p/ lista
        </button>

        <div style={{ marginLeft: 12, color: '#4b5563', fontSize: 13 }}>
          Arraste para o canvas:
        </div>

        <div
          draggable
          onDragStart={(e) => onDragStart(e, 'message')}
          style={chip}
          title="Mensagem"
        >
          Mensagem
        </div>
        <div draggable onDragStart={(e) => onDragStart(e, 'api')} style={chip} title="API">
          API
        </div>
        <div draggable onDragStart={(e) => onDragStart(e, 'code')} style={chip} title="Code">
          Code
        </div>
        <div
          draggable
          onDragStart={(e) => onDragStart(e, 'audience')}
          style={chip}
          title="Audiência"
        >
          Audiência
        </div>
        <div
          draggable
          onDragStart={(e) => onDragStart(e, 'delay')}
          style={chip}
          title="Delay"
        >
          Delay
        </div>
        <div draggable onDragStart={(e) => onDragStart(e, 'end')} style={chip} title="End">
          End
        </div>

        <input
          value={flowName}
          onChange={(e) => setFlowName(e.target.value)}
          placeholder="Nome do flow"
          style={{
            marginLeft: 16,
            border: '1px solid #e5e7eb',
            borderRadius: 8,
            padding: '8px 12px',
            minWidth: 220,
          }}
        />

        <button className="btn" style={{ marginLeft: 'auto' }} onClick={autoLayout}>
          Auto-layout
        </button>

        <label className="btn secondary" style={{ cursor: 'pointer' }}>
          Importar
          <input type="file" accept="application/json" onChange={importFlow} style={{ display: 'none' }} />
        </label>

        <button className="btn secondary" onClick={exportFlow}>
          Exportar
        </button>

        <button className="btn" onClick={saveFlow}>
          Salvar flow
        </button>
      </div>

      {/* Canvas */}
      <div style={{ position: 'relative' }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          nodeTypes={nodeTypes}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          onSelectionChange={onSelectionChange}
          onNodeContextMenu={onNodeContextMenu}
          onInit={setRf}
          onDrop={onDrop}
          onDragOver={onDragOver}
          minZoom={0.2}
          maxZoom={1.5}
          defaultViewport={{ x: 0, y: 0, zoom: 0.9 }}
          fitView
        >
          <MiniMap />
          <Controls />
          <Background gap={16} />
        </ReactFlow>

        {/* Context menu */}
        {ctxMenu && (
          <div
            style={{
              position: 'fixed',
              top: ctxMenu.y,
              left: ctxMenu.x,
              background: '#fff',
              border: '1px solid #e5e7eb',
              borderRadius: 8,
              boxShadow: '0 8px 24px rgba(0,0,0,.12)',
              overflow: 'hidden',
              zIndex: 60,
            }}
            onMouseLeave={ctxClose}
          >
            <button className="btn secondary" style={{ borderRadius: 0, width: 180 }} onClick={ctxRename}>
              Renomear
            </button>
            <button className="btn secondary" style={{ borderRadius: 0, width: 180 }} onClick={ctxDuplicate}>
              Duplicar
            </button>
            <button className="btn secondary" style={{ borderRadius: 0, width: 180 }} onClick={ctxReset}>
              Resetar
            </button>
            <button className="btn danger" style={{ borderRadius: 0, width: 180 }} onClick={ctxDelete}>
              Deletar
            </button>
          </div>
        )}

        {/* Drawer de propriedades */}
        <Drawer />
      </div>
    </div>
  );
}
